INSERT INTO `phome_ecms_article` VALUES (1, 55, 0, 1, 0, 0, '', '1', 2, 'test', 0, 1, 1, 0, 0, 0, 0, 1355124447, 1355124447, 1, 0, 0, '', '/ecms72/article/xiaoshuo/1.html', 1, 1, 1, '', '歷史上的父子宰相', 1355124447, 'http://cache.mars.sina.com.cn/nd/vipbook//bookcover/tiny85/9/cover_9d10c808811a441e144e7a81f19ff337.jpg', '', '在清朝著名的康乾盛世時期，中國安徽有一個非常有名氣的家族，為大清王朝盛世的到來做出了重大的貢獻，被人稱譽為「五里三進士」、「隔河兩狀元」。', '紀連海', '', '2012/1210/756251542d10f056296ba533ed4f5b45', 0);
INSERT INTO `phome_ecms_article` VALUES (2, 55, 0, 0, 0, 0, '', '2', 2, 'test', 0, 0, 1, 0, 0, 0, 0, 1355124448, 1355124448, 1, 0, 0, '', '/ecms72/article/xiaoshuo/2.html', 1, 1, 1, '', '清末重臣：張之洞大傳', 1355124448, 'http://www.sinaimg.cn/book/nzt/history/cha/zzddz/U1000P112T78D6187F1709DT20080410142439.jpg', '', '19世紀30年代末，西方資本主義侵略中國的前夕，是清王朝統治達到極端黑暗的年代。道光皇帝上台，曾想著振興一番。面對吏治敗壞、武備廢弛、農政不修的現實，他詔令各省興復書院...', '馬東玉', '', '2012/1210/cf0078d77809a73b50180e81ac267083', 0);
INSERT INTO `phome_ecms_article` VALUES (3, 55, 0, 0, 0, 0, '', '3', 2, 'test', 0, 1, 1, 0, 0, 0, 0, 1355124449, 1355124449, 1, 0, 0, '', '/ecms72/article/xiaoshuo/3.html', 1, 1, 1, '', '我的家', 1355124449, 'http://www.sinaimg.cn/book/http/book.sina.com.cn/nzt/history/sal/wodejia/U2016P112T78D5143F1709DT20071203142040.jpg', '', '日本作家水上勉去年九月訪問成都後，經上海回國。我在上海接待他，他告訴我他到過我的老家，看見一株枯樹和空蕩蕩的庭院。他不知道是什麼樹。他撫摩著粗糙的樹皮，想像過去的事情。', '巴金', '', '2012/1210/8fdc40025cf81890cfa5dbe40b08c0bd', 0);
INSERT INTO `phome_ecms_article` VALUES (4, 55, 0, 6, 0, 0, '', '4', 2, 'test', 0, 0, 1, 0, 0, 0, 0, 1355124450, 1355124450, 1, 0, 0, '', '/ecms72/article/xiaoshuo/4.html', 1, 1, 1, '', '晚清外交風雲人物', 1355124450, 'http://cache.mars.sina.com.cn/nd/vipbook//bookcover/tiny85/24/cover_6b3081eea2c4a49e100aa14a2ac4351d.jpg', '', '青春的末代帝國當下這個時代，當別人都忙著掙錢、忙著進行各種殘酷的生存鬥爭時，你如果像《大話西遊》裡的唐僧那樣呼喊「下雨了，大家要收衣服啦！」是不合時宜，會被人嗤笑的。', '李揚帆', '', '2012/1210/7649051c4311b3200fe7821a63ed4bcf', 0);
INSERT INTO `phome_ecms_article` VALUES (5, 55, 0, 0, 0, 0, '', '5', 2, 'test', 0, 0, 0, 0, 0, 0, 0, 1355124451, 1355124451, 1, 0, 0, '', '/ecms72/article/xiaoshuo/5.html', 1, 1, 1, '', '諾獎得主自傳：影中漫步', 1355124451, '', '', '作者簡介：多麗絲·萊辛，2007年諾貝爾文學獎獲得者。1919年10月22日出生在伊朗，父母都是英國人。六歲時，隨父母移居津巴布韋。', '多麗絲·萊辛', '', '2012/1210/cf65ff0526ee53ec95093f6c5344bce4', 0);
INSERT INTO `phome_ecms_article` VALUES (6, 55, 0, 3, 0, 0, '', '6', 2, 'test', 0, 0, 0, 0, 0, 0, 0, 1355124452, 1355124452, 1, 0, 0, '', '/ecms72/article/xiaoshuo/6.html', 1, 1, 1, '', '晚清重臣：曾國藩大傳', 1355124452, '', '', '「曾國藩現象」的歷史回顧,20世紀90年代初，中國出現了關心曾國藩的思想文化熱潮。長篇小說《曾國藩》為讀者搶購，弄得洛陽紙貴。《曾國藩家書》由出版商競相出版，百姓排隊購讀。', '馬東玉', '', '2012/1210/04354bd49c04ba41c63fb3c02519172e', 0);
INSERT INTO `phome_ecms_article` VALUES (7, 56, 0, 0, 0, 0, '', '7', 2, 'test', 0, 0, 0, 0, 0, 0, 0, 1355124453, 1355124453, 1, 0, 0, '', '/ecms72/article/sanwen/7.html', 1, 1, 1, '', '到底是上海人', 1355124453, '', '', ' 一年前回上海來，對於久違了的上海人的第一個印象是白與胖。在香港，廣東人十有八九是黝黑瘦小的，印度人還要黑，馬來人還要瘦。看慣了他們，上海人顯得個個肥白如瓠。', '張愛玲', '', '2012/1210/976aa99a96b2d142a78b7aa571a62d4e', 0);
INSERT INTO `phome_ecms_article` VALUES (8, 56, 0, 0, 0, 0, '', '8', 2, 'test', 0, 0, 0, 0, 0, 0, 0, 1355124454, 1355124454, 1, 0, 0, '', '/ecms72/article/sanwen/8.html', 1, 1, 1, '', '洋人看京戲及其他', 1355124454, '', '', '用洋人看京戲的眼光來看看中國的一切，也不失為一樁有意昧的事。頭上搭了竹竿，\r\n晾著小孩的開檔褲；櫃檯上的玻璃缸中盛著「參須露酒」；這一家的擴音機裡唱著梅蘭芳...', '張愛玲', '', '2012/1210/2105366fa3dc90e61c63ebe14bc02904', 0);
INSERT INTO `phome_ecms_article` VALUES (9, 56, 0, 0, 0, 0, '', '9', 2, 'test', 0, 0, 0, 0, 0, 0, 0, 1355124455, 1355124455, 1, 0, 0, '', '/ecms72/article/sanwen/9.html', 1, 1, 1, '', '更衣記', 1355124455, '', '', '如果當初世代相傳的衣服沒有大批賣給收舊貨的，一年一度六月裡曬衣裳，該是一\r\n件輝煌熱鬧的事罷。你在竹竿與竹竿之間走過，兩邊攔著續羅綢緞的牆——', '張愛玲', '', '2012/1210/72ba0f051b8c4eda17dad61ef96e1faf', 0);
INSERT INTO `phome_ecms_article` VALUES (10, 56, 0, 0, 0, 0, '', '10', 2, 'test', 0, 1, 0, 0, 0, 0, 0, 1355124456, 1355124456, 1, 0, 0, '', '/ecms72/article/sanwen/10.html', 1, 1, 1, '', '公寓生活記趣', 1355124456, '', '', '讀到「我欲乘風歸去，又恐瓊樓玉宇，高處不勝寒」的兩句詞，公寓房子上層的居\r\n民多半要感到毛骨驚然。屋子越高越冷。', '張愛玲', '', '2012/1210/cca5e8611ba5f8fa4d95c4ba324661ea', 0);
INSERT INTO `phome_ecms_article` VALUES (11, 56, 0, 0, 0, 0, '', '11', 2, 'test', 0, 1, 0, 0, 0, 0, 0, 1355124457, 1355124457, 1, 0, 0, '', '/ecms72/article/sanwen/11.html', 1, 1, 1, '', '夜營的喇叭', 1355124457, '', '', ' 晚上十點鐘，我在燈下看書，離家不遠的軍營裡的喇叭吹起了熟悉的調子。幾個簡\r\n單的音階，緩緩的上去又下來，在這鼎沸的大城市裡難得有這樣的簡單的心。', '張愛玲', '', '2012/1210/32b23fb072b8468689aaf02abb699840', 0);
INSERT INTO `phome_ecms_article` VALUES (12, 56, 0, 1, 0, 0, '', '12', 2, 'test', 0, 1, 0, 0, 0, 0, 0, 1355124458, 1355124458, 1, 0, 0, '', '/ecms72/article/sanwen/12.html', 1, 1, 1, '', '道路以目', 1355124458, '', '', '有個外國姑娘，到中國來了兩年，故宮、長城、東方蒙特卡羅、東方威尼斯，都沒\r\n瞻仰過，對於中國新文藝新電影似乎也缺乏興趣，然而她特別賞識中國小孩，說：「真美呀」', '張愛玲', '', '2012/1210/831edf79588dcf4e9d896b146b560af4', 0);
INSERT INTO `phome_ecms_article` VALUES (13, 57, 0, 1, 0, 0, '', '13', 2, 'test', 0, 0, 0, 0, 0, 0, 0, 1355124459, 1355124459, 1, 0, 0, '', '/ecms72/article/shige/13.html', 1, 1, 1, '', '我微笑著走向生活', 1355124459, '', '', '我微笑著走向生活， \r\n　　無論生活以什麼方式回敬我。 \r\n　　　 \r\n　　報我以平坦嗎？ \r\n　　我是一條歡樂奔流的小河。 \r\n......', '汪國真', '', '2012/1210/1a577647491829e080e1beed21b97d0e', 0);
INSERT INTO `phome_ecms_article` VALUES (14, 57, 0, 2, 0, 0, '', '14', 2, 'test', 0, 1, 0, 0, 0, 0, 0, 1355124460, 1355124460, 1, 0, 0, '', '/ecms72/article/shige/14.html', 1, 1, 1, '', '再別康橋', 1355124460, '', '', '輕輕的我走了，\r\n    　正如我輕輕的來；\r\n    我輕輕的招手，\r\n    　作別西天的雲彩。\r\n......', '徐志摩', '', '2012/1210/8ceba0075291748f3fb1f652601a945f', 0);
INSERT INTO `phome_ecms_article` VALUES (15, 57, 0, 1, 0, 0, '', '15', 2, 'test', 0, 0, 0, 0, 0, 0, 0, 1355124461, 1355124461, 1, 0, 0, '', '/ecms72/article/shige/15.html', 1, 1, 1, '', '斷章', 1355124461, '', '', '你站在橋上看風景\r\n看風景人在橋上看你\r\n\r\n明月裝飾了你的窗子\r\n你裝飾了別人的夢', '卞之琳', '', '2012/1210/d8b12d2c787d4db30a684056c51648c5', 0);
INSERT INTO `phome_ecms_article` VALUES (16, 57, 0, 1, 0, 0, '', '16', 2, 'test', 0, 0, 0, 0, 0, 0, 0, 1355124462, 1355124462, 1, 0, 0, '', '/ecms72/article/shige/16.html', 1, 1, 1, '', '面朝大海，春暖花開', 1355124462, '', '', '從明天起，做一個幸福的人\r\n餵馬，劈柴，周遊世界\r\n從明天起，關心糧食和蔬菜\r\n我有一所房子，面朝大海，春暖花開\r\n......', '海子', '', '2012/1210/3169dc2a3082642ddc4f84e5479f8cdf', 0);
INSERT INTO `phome_ecms_article` VALUES (17, 57, 0, 1, 0, 0, '', '17', 2, 'test', 0, 0, 0, 0, 0, 0, 0, 1355124463, 1355124463, 1, 0, 0, '', '/ecms72/article/shige/17.html', 1, 1, 1, '', '遠和近', 1355124463, '', '', '你，一會看我，一會看雲。我覺得你看我時很遠，你看雲時很近', '顧城', '', '2012/1210/bc338634ac1c5aa9bf516317606b00b4', 0);
INSERT INTO `phome_ecms_article` VALUES (18, 57, 0, 2, 0, 0, '', '18', 2, 'test', 0, 0, 0, 0, 0, 0, 0, 1355124464, 1355124464, 1, 0, 0, '', '/ecms72/article/shige/18.html', 1, 1, 1, '', '回答', 1355124464, '', '', '卑鄙是卑鄙者的通行證， \r\n高尚是高尚者的墓誌銘， \r\n看吧，在那鍍金的天空中， \r\n飄滿了死者彎曲的倒影。 \r\n...', '北島', '', '2012/1210/58bcb04ea86c0a15654648f49d738628', 0);
