INSERT INTO `phome_ecms_news` VALUES (1, 35, 0, 0, 0, 0, '2012-12-10', '1', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124441, 1355124441, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/1.html', 1, 1, 1, '', '奧地利「獸父」是怎麼養成的？', 1355124441, '', '', '現年73歲的奧地利男子約瑟夫·弗萊茨勒把親生女兒囚禁在地牢裡達24年，持續性虐待女兒並與其生下7名子女。這起醜聞震驚了世界。', 0);
INSERT INTO `phome_ecms_news` VALUES (2, 35, 0, 0, 0, 0, '2012-12-10', '2', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124442, 1355124442, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/2.html', 1, 1, 1, '', '韓國總統李明博想請比爾·蓋茨當顧問', 1355124442, '', '', '韓國總統李明博決定聘請10位重量級外國人士擔任國際顧問，包括微軟創辦人比爾·蓋茨及一位外國元首。李明博下令修改韓國《國家公務員法》，延聘外國人擔任公務員、副部長甚至部長。', 0);
INSERT INTO `phome_ecms_news` VALUES (3, 35, 0, 0, 0, 0, '2012-12-10', '3', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124443, 1355124443, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/3.html', 1, 1, 1, '', '奧巴馬北卡初選大贏 希拉裡印第安納險勝', 1355124443, '', '', '美國總統競選人奧巴馬和希拉裡在北卡羅來納州和印第安納州預選各贏一場，其中奧巴馬以56%的得票率領先希拉裡14個百分點；印第安納州，希拉裡以51%險勝，而奧巴馬僅2個百分點落後。', 0);
INSERT INTO `phome_ecms_news` VALUES (4, 35, 0, 0, 0, 0, '2012-12-10', '4', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124444, 1355124444, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/4.html', 1, 1, 1, '', '普京獲俄總理提名 俄國家杜馬今日將審議', 1355124444, '', '', '普京在克里姆林宮與俄國家杜馬議會黨團領導人就他出任總理問題進行了磋商。據俄塔社報道，普京表示，一旦國家杜馬批准他為政府總理，他將考慮議員們提出的各種意見。', 0);
INSERT INTO `phome_ecms_news` VALUES (5, 35, 0, 0, 0, 0, '2012-12-10', '5', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124445, 1355124445, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/5.html', 1, 1, 1, '', '法國客機飛行員炫耀高空特技險致兩機相撞', 1355124445, '', '', '法國航空公司一名飛行員駕機途中讓一男孩到駕駛艙參觀,還為他「表演」高空特技，飛機在空中跳「搖擺舞」，險些造成兩機相撞。法國航空安全部門已就此事展開調查。', 0);
INSERT INTO `phome_ecms_news` VALUES (6, 35, 0, 0, 0, 0, '2012-12-10', '6', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124446, 1355124446, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/6.html', 1, 1, 1, '', '斑鱉全球僅三隻 兩隻蘇州完婚(視頻)', 1355124446, '', '', '瀕危動物斑鱉在全球只發現3只。長沙一隻斑鱉抵達蘇州動物園，與蘇州斑鱉配對。據介紹，雖然這三隻斑鱉都已老年，但飼養好能活三百年。若斑鱉能配對成功，將是保護斑鱉的最後希望。', 0);
INSERT INTO `phome_ecms_news` VALUES (7, 35, 0, 0, 0, 0, '2012-12-10', '7', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124447, 1355124447, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/7.html', 1, 1, 1, '', '日本本州東海岸發生7.1級地震 連發6級以上地震', 1355124447, '', '', '日本東部海域當地時間8日凌晨1時02分發生裡氏6.3級地震，1時45分又發生6.7級地震。北海道沿岸和本州東部地區都有震感，其中□木縣震感最強，東京也有明顯震感，晃動持續了近20秒。', 0);
INSERT INTO `phome_ecms_news` VALUES (8, 35, 0, 0, 0, 0, '2012-12-10', '8', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124448, 1355124448, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/8.html', 1, 1, 1, '', '七國人道援助物資已運抵緬甸', 1355124448, '', '', '包括中國在內的部分國家人道主義援助物資7日緊急運抵緬甸仰光市，緬甸政府迅速將有關物資運往災區發放給災民。緬甸國家電台當天晚間廣播說，已經有7個國家將救災物資運送到', 0);
INSERT INTO `phome_ecms_news` VALUES (9, 35, 0, 0, 0, 0, '2012-12-10', '9', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124449, 1355124449, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/9.html', 1, 1, 1, '', '聯合國糧農組織擔心強風暴影響緬甸稻米出口', 1355124449, '', '', '聯合國糧食7日表示，強熱帶風暴「納爾吉斯」給緬甸主要稻米產區造成重大損失，可能影響緬甸今年的稻米出口。糧農組織預計，緬甸受災最嚴重5個省邦的稻米產量佔全國總產量的65%', 0);
INSERT INTO `phome_ecms_news` VALUES (10, 35, 0, 0, 0, 0, '2012-12-10', '10', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124450, 1355124450, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/10.html', 1, 1, 1, '', '加拿大一公寓樓發生火災3人喪生數十人受傷', 1355124450, '', '', '加拿大不列顛哥倫比亞省北溫哥華市警方7日說，該市一棟3層公寓樓6日晚發生火災，造成3人喪生，數十人受傷。警方懷疑這是一起縱火案。', 0);
INSERT INTO `phome_ecms_news` VALUES (11, 35, 0, 1, 0, 0, '2012-12-10', '11', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124451, 1355124451, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/11.html', 1, 1, 1, '', '兩名日本女遊客在也門被部落武裝人員綁架', 1355124451, '', '', '日本駐也門大使館7日證實，兩名日本女遊客當天在也門中部城鎮馬裡卜被綁架。另據報道，路邊檢查站的警察試圖阻止綁匪逃走時受到綁匪攻擊，導致一名警察受傷。', 0);
INSERT INTO `phome_ecms_news` VALUES (12, 35, 0, 0, 0, 0, '2012-12-10', '12', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124452, 1355124452, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/12.html', 1, 1, 1, '', '愛爾蘭議會投票推舉布賴恩-考恩為新政府總理', 1355124452, '', '', '　　新華網快訊：愛爾蘭議會7日投票推舉副總理兼財政部長布賴恩·考恩為新一任政府總理。　　(本文來源：新華網  )', 0);
INSERT INTO `phome_ecms_news` VALUES (13, 35, 0, 0, 0, 0, '2012-12-10', '13', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124453, 1355124453, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/13.html', 1, 1, 1, '', '馬來紅新月會宣佈將向緬甸派出救災小組', 1355124453, '', '', '馬來西亞紅新月會7日在吉隆坡宣佈，將派出一個10人救災小組前往緬甸，幫助開展救災工作。', 0);
INSERT INTO `phome_ecms_news` VALUES (14, 35, 0, 0, 0, 0, '2012-12-10', '14', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124454, 1355124454, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/14.html', 1, 1, 1, '', '印度成功試射一枚「烈火－3」型導彈', 1355124454, '', '', '印度於當地時間7日成功試射了一枚「烈火-3」型導彈。這種讓印度人引以為豪的中程彈道導彈的射程可達3500公里，覆蓋中國內陸。', 0);
INSERT INTO `phome_ecms_news` VALUES (15, 35, 0, 0, 0, 0, '2012-12-10', '15', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124455, 1355124455, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/15.html', 1, 1, 1, '', '中國緊急援助物資運抵緬甸仰光(組圖)', 1355124455, '', '', '中國政府向緬甸政府提供的人道主義緊急援助物資7日下午空運抵達仰光國際機場。', 0);
INSERT INTO `phome_ecms_news` VALUES (61, 34, 0, 0, 0, 0, '2012-12-10', '61', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124501, 1355124501, 1, 0, 0, '', '/ecms72/news/china/2012-12-10/61.html', 1, 1, 1, '', '台\\"金援外交\\"醜聞涉案人遭到恐嚇 警方加強保護', 1355124501, '', '', '涉入侵吞台當局「金援外交」醜聞的中間人吳思材驚爆遭兩名歹徒恐嚇要求離開台灣。台灣刑事部門3日晚間已針對全案展開調查，並24小時採取貼身保護的方式，確保吳思材的人身安全。', 0);
INSERT INTO `phome_ecms_news` VALUES (62, 34, 0, 1, 0, 0, '2012-12-10', '62', 1, 'admin', 0, 1, 0, 0, 0, 0, 0, 1355124502, 1355124502, 1, 0, 0, '', '/ecms72/news/china/2012-12-10/62.html', 1, 1, 1, '', '武漢一日兩遭暴雨襲擊 閃電2000次2人死亡(圖)', 1355124502, '', '', '昨晚武昌上空電閃雷鳴。武漢共閃電2200次。本報攝昨日凌晨1點，一場強雷暴「意外」襲擊江城。來源：武漢晚報熊波攝昨晚9時，我市突降大暴雨，鐵路立交橋下路人打著傘在雨中急行。', 0);
INSERT INTO `phome_ecms_news` VALUES (63, 34, 0, 0, 0, 0, '2012-12-10', '63', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124503, 1355124503, 1, 0, 0, '', '/ecms72/news/china/2012-12-10/63.html', 1, 1, 1, '', 'EV71病毒襲粵港澳 香港發病率高於過去3年', 1355124503, '', '', '據香港文匯報報道，近日，包括安徽、廣東、湖北、陝西、浙江等省份相繼爆出手足口病疫情，引起中央高度關注。其中安徽阜陽已有22人死亡，而鄰近香港的廣東佛山也有2名病例死亡。', 0);
INSERT INTO `phome_ecms_news` VALUES (64, 34, 0, 0, 0, 0, '2012-12-10', '64', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124504, 1355124504, 1, 0, 0, '', '/ecms72/news/china/2012-12-10/64.html', 1, 1, 1, '', '民進黨今開臨時黨代會檢討敗選 陳水扁選擇缺席', 1355124504, '', '', '民進黨今天下午召開臨時黨代表大會，對敗選後黨內檢討總結，台當局「副總統」呂秀蓮、黨主席謝長廷、「行政院長」張俊雄及兩位前黨主席蘇貞昌及游錫坤等將出席，唯獨陳水扁缺席。', 0);
INSERT INTO `phome_ecms_news` VALUES (65, 34, 0, 0, 0, 0, '2012-12-10', '65', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124505, 1355124505, 1, 0, 0, '', '/ecms72/news/china/2012-12-10/65.html', 1, 1, 1, '', '柏楊葬禮將於14日舉行 骨灰拋撒綠島海面', 1355124505, '', '', '在台北濟南教會的教堂裡為柏楊舉行「安息禮拜」活動，教堂有300個座位，不發請柬，但台灣甚至海外各界名流會到場共同紀念這位文化大師。紀念結束後，柏楊的遺體將送去火化。', 0);
INSERT INTO `phome_ecms_news` VALUES (66, 34, 0, 0, 0, 0, '2012-12-10', '66', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124506, 1355124506, 1, 0, 0, '', '/ecms72/news/china/2012-12-10/66.html', 1, 1, 1, '', '廣東省物價局：糧價節後上漲不可信', 1355124506, '', '', '廣東省物價局就近期糧價以及所謂節後糧價要漲的消息作出回應。省物價局監測報告以及五一期間市場檢查，目前大米批零市場供應正常，僅個別國產優質米零售價格漲幅較大，進口泰國米', 0);
INSERT INTO `phome_ecms_news` VALUES (67, 34, 0, 1, 0, 0, '2012-12-10', '67', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124507, 1355124507, 1, 0, 0, '', '/ecms72/news/china/2012-12-10/67.html', 1, 1, 1, '', '杭州灣跨海大橋日均車流量逾10萬(組圖)', 1355124507, '', '', '大橋開通頭兩天日均約有逾10萬多車次通行。在通行首日時，很大一部分車輛是為「嘗鮮」而來，不僅車流量很大，且這些車輛常會在橋上停車觀光，成為大橋擁堵的重要原因。', 0);
INSERT INTO `phome_ecms_news` VALUES (68, 34, 0, 1, 0, 0, '2012-12-10', '68', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124508, 1355124508, 1, 0, 0, '', '/ecms72/news/china/2012-12-10/68.html', 1, 1, 1, '', '駐日大使崔天凱:胡錦濤主席訪日有三點值得關注', 1355124508, '', '', '中國駐日本大使崔天凱在人民日報撰文指出：胡錦濤主席訪日有三點尤其值得關注。中國國家主席胡錦濤將對日本進行國事訪問。這是中國國家元首時隔十年再次訪日，也是新世紀首次訪日', 0);
INSERT INTO `phome_ecms_news` VALUES (69, 34, 0, 23, 0, 0, '2012-12-10', '69', 1, 'admin', 0, 0, 0, 0, 0, 0, 0, 1355124509, 1355124509, 1, 0, 0, '', '/ecms72/news/china/2012-12-10/69.html', 1, 1, 1, '', '廣東丹霞山發現巨型\\"青銅劍\\"(組圖)', 1355124509, '', '', '「青銅劍」「鑲嵌」在曬布巖巖壁上。「青銅劍」疑似綠色植物攀附在巖壁上生長。記者陪同遠道而來的朋友遊覽世界地質公園廣東丹霞山，在陽元石景區內，突然發現一把青銅劍', 0);
INSERT INTO `phome_ecms_news` VALUES (70, 35, 0, 2, 0, 0, '2012-12-10', '70', 1, 'admin', 1, 1, 1, 0, 0, 0, 0, 1355124510, 1355124510, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/70.html', 1, 1, 1, '', '俄羅斯第三任總統梅德韋傑夫宣誓就職', 1355124510, 'http://cimg21.163.com/cnews/2008/5/7/20080507165459698f2.jpg', '', '在3月2日俄羅斯總統選舉中勝出的當選總統德米特裡-梅德韋傑夫, 於當地時間7日12時過後(北京時間下午4時)在克里姆林宮正式宣誓就職，成為俄羅斯第三位總統。', 0);
INSERT INTO `phome_ecms_news` VALUES (71, 37, 0, 1, 0, 0, '2012-12-10', '71', 1, 'admin', 1, 0, 1, 0, 0, 0, 0, 1355124511, 1355124511, 1, 0, 0, '', '/ecms72/news/sports/2012-12-10/71.html', 1, 1, 1, '', '奧運聖火成功登頂珠峰', 1355124511, 'http://cimg20.163.com/cnews/2008/5/8/2008050809305433312.jpg', '', '奧運火炬、接力珠峰傳遞活動圓滿結束，攜帶聖火的突擊隊員全部登頂，並進行了聖火的傳遞。聖火在珠峰之巔傳遞，首次成功點亮世界最高峰，創造了奧運聖火登上世界第三極的新紀錄。', 0);
INSERT INTO `phome_ecms_news` VALUES (72, 35, 0, 11, 0, 0, '2012-12-10', '72', 1, 'admin', 1, 0, 1, 0, 0, 0, 0, 1355124512, 1355124512, 1, 0, 0, '', '/ecms72/news/world/2012-12-10/72.html', 1, 1, 1, '', '中國緊急援助物資運抵緬甸仰光(組圖)', 1355124512, 'http://cimg21.163.com/cnews/2008/5/8/200805080616347228c.jpg', '', '中國政府向緬甸提供的人道主義緊急援助物資當日空運抵達仰光國際機場。這批物資主要包括帳篷、毛巾被和壓縮餅乾等，價值50萬美元。中國政府還向緬甸提供了50萬美元的現匯援助。', 0);
INSERT INTO `phome_ecms_news` VALUES (73, 36, 0, 0, 0, 0, '2012-12-10', '73', 1, 'admin', 0, 1, 1, 0, 0, 0, 0, 1355124513, 1355124513, 1, 0, 0, '', '/ecms72/news/ent/2012-12-10/73.html', 1, 1, 1, '', '張曼玉廣告寫真花絮曝光 流露優雅從容', 1355124513, 'http://cimg21.163.com/ent/2008/5/8/2008050811191076090.jpg', '', '張曼玉最近一組廣告寫真花絮曝光，照片中的張曼玉風采依舊，透露優雅氣質。', 0);
INSERT INTO `phome_ecms_news` VALUES (74, 36, 0, 0, 0, 0, '2012-12-10', '74', 1, 'admin', 0, 1, 1, 0, 0, 0, 0, 1355124514, 1355124514, 1, 0, 0, '', '/ecms72/news/ent/2012-12-10/74.html', 1, 1, 1, '張藝謀', '傳張藝謀因執導奧運身價漲5倍', 1355124514, 'http://cimg21.163.com/ent/2008/5/8/200805080924559c424.jpg', '', '2008北京奧運會的開幕式已開始了綵排，作為總導演的張藝謀坦言，執導奧運會的壓力遠大過執導電影。而由於導演了奧運會這樣的人類盛典，張藝謀導演身價上漲了5倍以上。', 0);
INSERT INTO `phome_ecms_news` VALUES (75, 36, 0, 0, 0, 0, '2012-12-10', '75', 1, 'admin', 0, 1, 1, 0, 0, 0, 0, 1355124515, 1355124515, 1, 0, 0, '', '/ecms72/news/ent/2012-12-10/75.html', 1, 1, 1, '尹馨', '尹馨大膽亮相《男人裝》 嫩膚美腿勾人魂', 1355124515, 'http://img1.qq.com/ent/pics/10290/10290789.jpg', '', '蔡明亮徒弟之作《幫幫我愛神》女主角尹馨的大膽出演，足以挑戰《色戒》中湯唯的情色。尹馨在5月份的《男人裝》中有出色亮相。', 0);
INSERT INTO `phome_ecms_news` VALUES (76, 36, 0, 0, 0, 0, '2012-12-10', '76', 1, 'admin', 0, 1, 1, 0, 0, 0, 0, 1355124516, 1355124516, 1, 0, 0, '', '/ecms72/news/ent/2012-12-10/76.html', 1, 1, 1, '', '「最美清潔工」原是《赤壁》宮女', 1355124516, 'http://img1.qq.com/ent/pics/10290/10290791.jpg', '', '2008北京車展4月23日剛剛落幕，「最美清潔工」迅速走紅網絡。這位躲在車展角落毫不起眼的清潔工美女，在有心人士的偷拍下，照片一經放到網上就引來網友熱捧。', 0);
INSERT INTO `phome_ecms_news` VALUES (77, 37, 0, 0, 0, 0, '2012-12-10', '77', 1, 'admin', 0, 1, 1, 0, 0, 0, 0, 1355124517, 1355124517, 1, 0, 0, '', '/ecms72/news/sports/2012-12-10/77.html', 1, 1, 1, '科比', '科比專為大場面而生', 1355124517, 'http://img1.qq.com/sports/pics/10300/10300435.jpg', '', '成名這麼久遠了，科比終於被證實「最有價值」。', 0);
INSERT INTO `phome_ecms_news` VALUES (78, 37, 0, 28, 0, 0, '2012-12-10', '78', 1, 'admin', 0, 1, 1, 0, 0, 0, 0, 1355124518, 1355124518, 1, 0, 0, '', '/ecms72/news/sports/2012-12-10/78.html', 1, 1, 1, '', '中國男乒第16次捧起斯韋思林杯', 1355124518, 'http://img1.qq.com/sports/pics/8865/8865651.jpg', '', '第49屆世乒賽男團決賽的爭奪，中國隊最終憑借馬琳、王皓和王勵勤三員虎將的出色發揮以3比0乾淨利落的擊敗韓國隊，延續了世乒賽上逢韓必勝的歷史，歷史上第16次捧起斯韋思林杯！', 0);
INSERT INTO `phome_ecms_news_data_1` VALUES (1, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<center><img alt=\\"\\" border=\\"0\\" src=\\"http://cimg20.163.com/cnews/2008/5/8/20080508020143c7ed9.jpg\\" /></center><center>約瑟夫&middot;弗萊茨勒 </center><center>&nbsp;</center><center><img alt=\\"\\" border=\\"0\\" src=\\"http://cimg21.163.com/cnews/2008/5/8/20080508020154969f9.jpg\\" /></center><center>約瑟夫年輕時的留影。 </center>\r\n<p style=\\"text-indent: 2em\\"><strong>新快報5月8日報道</strong>&nbsp; 成長於二戰時納粹統治下的第三帝國，童年時經常遭受單親母親的殘酷虐待，衍生出一面具備良好教養一面則表現出強烈權力情結的雙重人格，最終製造出震驚世界的&ldquo;奧地利獸父囚女&rdquo;案&mdash;&mdash;</p>\r\n<p style=\\"text-indent: 2em\\">4月19日，星期六。在阿姆施泰滕鎮，一個19歲少女被人發現倒在當地人約瑟夫&middot;弗萊茨勒的家門口，屋主約瑟夫隨即召來了救護車。少女被送抵醫院時，臉色非常蒼白，而且舌頭在流血。入院1小時後，約瑟夫趕到並告訴醫生阿波特&middot;雷特，這孩子的母親&mdash;&mdash;也就是自己的女兒伊麗莎白&mdash;&mdash;無法且不願意照料她，於是將她丟棄在他家門口。約瑟夫說，伊麗莎白留下了一張紙條，上面寫著這個叫克斯廷的女孩患有嚴重的頭痛症，服用阿司匹林後出現驚厥。說完這番話約瑟夫就離開了，他說自己還有家庭需要照顧。</p>\r\n<p style=\\"text-indent: 2em\\">克斯廷的病情迅速惡化，並漸漸陷入昏迷。醫生表示亟需她的母親親自過來，以告知病人的病史，但未見任何回復。</p>\r\n<p style=\\"text-indent: 2em\\">直到一周後的4月26日，弗萊茨勒家失蹤24年的女兒伊麗莎白終於在父親的陪同下出現在醫院，懷疑克斯廷遭到虐待的警方遂將他們拘捕。</p>\r\n<p style=\\"text-indent: 2em\\">警察將伊麗莎白隔離問訊。從一開始，她就表現得非常奇怪。年僅42歲的她頭髮灰白，皮膚也白得嚇人，看上去像剛從養老院中出來的60歲老婦。她也很緊張，並突然問警方是否能保證她與她的孩子永遠不會再見到約瑟夫。得到警方的保證後，她開始訴說一個被隱藏了24年的恐怖故事&mdash;&mdash;也就是接下來數周佔據全球各大媒體頭版、震驚世界的&ldquo;奧地利獸父囚女&rdquo;案。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>&ldquo;阿姆施泰滕鎮的好男人&rdquo;</strong></p>\r\n<p style=\\"text-indent: 2em\\">弗萊茨勒家最美麗的女兒伊麗莎白11歲起便開始遭到親生父親約瑟夫性侵犯。她從16歲起兩次離家出走，以試圖逃出父親魔爪，但均未能成功。18歲那年，她長達24年的噩夢開始了&mdash;&mdash;約瑟夫將她迷暈後囚禁在經過精心裝修過的地牢裡。24年間，她一共生下了7個約瑟夫的孩子，除了1個生出來不久後夭折，3個被約瑟夫帶走正常撫養長大外，伊麗莎白與剩下3個孩子就一直住在不見天日的地下室裡。孩子們從未見過外面的世界，也未呼吸過外面的空氣。他們既不知自由為何物，也不懂任何社會規則。他們唯一能見到的人就是每日透過窗口給他們送飯、經常強姦母親、肆意打罵恐嚇他們的禽獸外公兼父親約瑟夫。</p>\r\n<p style=\\"text-indent: 2em\\">而這樣一個人，在阿姆施泰滕鎮卻是有口皆碑的居家好男人。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>&ldquo;每個人都相信他的說辭&rdquo;</strong></p>\r\n<p style=\\"text-indent: 2em\\">與約瑟夫交好的拉斯博格市副市長拉波&middot;斯圖特茨是這麼形容他的：&ldquo;弗萊茨勒在我眼中是一個聰明、成功的男人。他總愛談論他完美的家庭，但他對子女非常嚴厲。&rdquo;</p>\r\n<p style=\\"text-indent: 2em\\">46歲的霍斯特&middot;赫爾鮑爾是約瑟夫47歲的二女兒羅斯瑪麗的丈夫，此案曝光之前，他經常上門拜訪岳父約瑟夫與岳母老羅斯瑪麗（母女同名）。</p>\r\n<p style=\\"text-indent: 2em\\">赫爾鮑爾說，過去他完全相信岳父所說的關於伊麗莎白被邪教所迷惑而離家出走的故事，而完全沒有想到，可憐的伊麗莎白就在自己腳下艱難生存。&ldquo;我們每個人都相信他的說辭，&rdquo;他說，&ldquo;即使是突然出現了3個孩子，且被這個家庭所收養，我們也未曾萌生過一點兒疑問。&rdquo;</p>\r\n<p style=\\"text-indent: 2em\\">&ldquo;他無論是在家還是工作都很勤勞，這個家庭完全看不出有什麼問題。&rdquo;他說，&ldquo;他待人友善，在鄰居之中口碑很好，這令人實在很難接受現在這樣的事實。&rdquo;</p>\r\n<p style=\\"text-indent: 2em\\"><strong>&ldquo;獸父&rdquo;幼時常遭母親虐待</strong></p>\r\n<p style=\\"text-indent: 2em\\">有證據顯示，弗萊茨勒從小便受到單身母親的殘忍虐待，或許這就是令他變成一個禽獸的真正原因。</p>\r\n<p style=\\"text-indent: 2em\\">據弗萊茨勒的妻妹、現年56歲的克莉斯汀&middot;羅斯瑪麗透露，弗萊茨勒由單身母親撫養長大，他的母親脾氣暴躁，愛對弗萊茨勒施暴。&ldquo;她每天都把他打得鼻青臉腫，這很可能是造成他心理變態的原因。他不能同情他人，一輩子都在羞辱我姐姐&rdquo;。</p>\r\n<p style=\\"text-indent: 2em\\">弗萊茨勒生於1935年4月9日，二戰爆發時年僅4歲。目前尚不清楚他的父親是否在二戰期間死亡，阿姆施泰滕鎮的戰爭陣亡名單上有個叫法蘭茨&middot;弗萊茨勒的男人，但鎮委會上周拒絕承認此人是約瑟夫的父親。</p>\r\n<p style=\\"text-indent: 2em\\">只能說，約瑟夫的童年就在奧地利被蘇聯軍隊佔領的那段歲月裡度過。據說當時有許多德國婦女和奧地利婦女遭到蘇軍士兵強姦。</p>\r\n<p style=\\"text-indent: 2em\\">約瑟夫的人生架構開始變得清晰是自他成年後。他在技工學校學習了電子工程，畢業後在一家鋼鐵公司工作。21歲那年，他與17歲的羅斯瑪麗共結連理，並生下7個孩子。1969年至1971年間，他在阿姆施泰滕鎮一家建材公司工作，老闆評價他是一名&ldquo;聰明善良的技工&rdquo;。後來，他跳槽到一家德國公司做銷售。1973年，他與妻子在山上買了一座旅館，並以此為生。有了一定殷實家底後，約瑟夫購置了別處房產，遂把原來的別墅出租。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>童年遭遇衍生權力情結</strong></p>\r\n<p style=\\"text-indent: 2em\\">據奧地利心理學家雷哈德&middot;海勒向法院提供的心理分析報告顯示，約瑟夫的權力情結或源自母親對其的虐待。</p>\r\n<p style=\\"text-indent: 2em\\">另一名在法庭上作證的心理學家西格朗&middot;羅斯曼尼指出，弗萊茨勒有雙重性格，其中一重性格被向別人施加完全控制的需要所主導。自18歲起便被他囚禁在地牢中的女兒伊麗莎白就是他權力情結的受害者。&ldquo;她是弗萊茨勒可以在任意時候虐待的奴隸，&rdquo;西格朗說，&ldquo;他令她順從，對她實施絕對的控制。&rdquo;</p>\r\n<p style=\\"text-indent: 2em\\">據奧地利城市林茨一名與約瑟夫相熟的酒保稱，約瑟夫是當地一所妓院的常客，但是妓女們都不太願意接待他，理由是他屬於&ldquo;嫖客中脾氣最古怪的2％&rdquo;。</p>\r\n<p style=\\"text-indent: 2em\\">這名自稱名叫克裡斯托弗&middot;R的男子對當地報紙說：&ldquo;我在這所妓院工作超過6年，弗萊茨勒是這裡的常客。他對任何人的態度都很霸道。一開始他會請女孩們喝酒，但過一會兒他就一副學校老師的模樣，突然厲聲厲色對人下命令，例如說&lsquo;坐直了！&rsquo;或&lsquo;別用那種口氣說話！&rsquo;等。&rdquo;</p>\r\n<p style=\\"text-indent: 2em\\">克裡斯托弗&middot;R說，95％的嫖客都是&ldquo;正常人&rdquo;，而另有3％的客人性格上多少有點反覆無常，&ldquo;但弗萊茨勒是屬於那2％最為極端最為反常的顧客一類，表面上看起來沒啥，其實內在很變態&rdquo;。</p>\r\n<p style=\\"text-indent: 2em\\">若不是克斯廷的病情突然惡化，約瑟夫不得不將她送往醫院，這件駭人聽聞的亂倫案，或許永無大白於天下的一日。試想一下，已經73歲的約瑟夫&mdash;&mdash;這是一個已到可能心臟病發作和中風的年齡&mdash;&mdash;突然某天就這麼死去了，被關在地牢裡的伊麗莎白和她的孩子們只能慢慢地餓死。不知道要再等多少年後他們的遺骸才會被人們發現，10年？20年？或許永遠不會被發現。這件令人毛骨悚然的秘密也將隨著&ldquo;居家好男人&rdquo;約瑟夫&middot;弗萊茨勒的死而永埋地底。（路易）</p>\r\n<p style=\\"text-indent: 2em\\"><strong>奧地利掀起性罪行&ldquo;嚴打&rdquo;潮</strong></p>\r\n<p>&nbsp;&nbsp;&nbsp; 奧地利政界就此案展開激烈討論</p>\r\n<p>　　奧地利警方首次承認，早在約瑟夫&middot;弗萊茨勒編造女兒受邪教蠱惑離家出走，並遺棄三個孩子要他撫養謊言時，警方就應該發現疑點。</p>\r\n<p>　　奧地利司法部長瑪利亞&middot;伯傑日前首次就此案發表聲明，承認警方的執法確實存在一定缺陷，她說：&ldquo;我認為，當時有關機構對約瑟夫的說辭過於輕信了，特別是在約瑟夫將女兒的失蹤歸咎於邪教時。這種事情若放在今天，應該會獲得更詳細的調查的。&rdquo;</p>\r\n<p>　　約瑟夫曾於1967年犯下強姦案並被定罪，在監獄裡服刑18個月，但在他辦理三個&ldquo;孫兒孫女&rdquo;的收養手續時，有關機構卻並沒有發現這一犯罪記錄。&ldquo;獸父囚女&rdquo;案在奧地利乃至全世界範圍內都掀起軒然大波，奧地利政府議員據此展開了是否提高對性犯罪者的嚴懲程度，例如公開他們的犯罪記錄，根據現行的奧地利法律，性犯罪記錄可於15年後從犯案者檔案上抹除，這就是為何雖然約瑟夫曾經有過強姦罪案底，卻能順利辦妥3個孩子的收養手續的原因。</p>\r\n<p>　　奧地利兩個激進政治團體&mdash;&mdash;&ldquo;奧地利未來聯盟&rdquo;以及&ldquo;自由之黨&rdquo;的議員們甚至呼籲對重複犯案的性罪犯進行&ldquo;閹割&rdquo;，並要求立法定期對兒童展開體檢，以確保他們沒有遭受性侵犯與虐待。</p>\r\n<p>　　而主流黨派則建議，延長強姦罪犯的服刑期限。（方海） (本文來源：金羊網-新快報 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (2, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<p style=\\"text-indent: 2em\\"><strong>新快報5月8日報道</strong> 韓國總統李明博已決定聘請10多位重量級外國人士，擔任國際顧問，其中包括電腦軟體業巨擘、微軟公司創辦人之一的比爾&middot;蓋茨以及一位外國元首，但該外國元首將以&ldquo;韓國的朋友&rdquo;稱呼，取代顧問頭銜。</p>\r\n<p style=\\"text-indent: 2em\\">據韓國媒體報道，青瓦台官員日前曾表示，李明博總統有意舉行一次類似&ldquo;經筵&rdquo;的活動。所謂經筵，是中國宋代制度，專為皇帝研讀經史開設講席，而陪伴皇帝研讀的，都是&ldquo;清要顯美之官&rdquo;。</p>\r\n<p style=\\"text-indent: 2em\\">韓國媒體稱，近代韓國已經沒有歷史上的國師或王師，但李明博希望自己在國內外都有許多老師，韓國人民也希望看到姿態很謙遜的總統。</p>\r\n<p style=\\"text-indent: 2em\\">李明博認為，韓國缺少國際金融與全球商務專業人才，故有聘用外籍人士擔任韓國公職的構想；而他已下令研議修改韓國《國家公務員法》，延聘外國人擔任韓國公務員、副部長甚至部長。 (本文來源：金羊網-新快報 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (3, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<center><img alt=\\"\\" border=\\"0\\" src=\\"http://cimg20.163.com/cnews/2008/5/8/200805080150359aa3b.jpg\\" /></center><center><br />\r\n<center><img alt=\\"\\" border=\\"0\\" src=\\"http://cimg20.163.com/cnews/2008/5/8/20080508015049021f2.jpg\\" /><br />\r\n</center></center>\r\n<p style=\\"text-indent: 2em\\"><strong>新快報5月8日報道</strong>&nbsp; 美國民主黨總統競選人奧巴馬和希拉裡在６日舉行的北卡羅來納州和印第安納州總統預選中平分秋色，各贏一場。</p>\r\n<p style=\\"text-indent: 2em\\">在北卡羅來納州，伊利諾伊州參議員奧巴馬以56%的得票率領先紐約州參議員希拉裡14個百分點；在印第安納州，希拉裡則以51%的得票率險勝，而奧巴馬僅以2個百分點落後。</p>\r\n<p style=\\"text-indent: 2em\\">北卡羅來納州選舉委員會官員說，此次民主黨選民投票率達到５０％，遠超往屆預選的１５％至３０％。印第安納州官員也說，今年預選投票率創歷史紀錄。印第安納州和北卡羅來納州預選將產生１８７名在民主黨全國代表大會上投票的代表，對於奧巴馬和希拉裡來說都十分關鍵。</p>\r\n<p style=\\"text-indent: 2em\\">目前，奧巴馬和希拉裡獲得的民主黨全國代表大會代表席位數分別為１７６８席和１６２２席。輿論普遍認為，兩人在６月３日預選階段結束前，都不太可能獲得贏得提名所需的至少２０２４個代表支持。因此，在很大程度上，兩人中誰是代表民主黨的最佳人選，要由佔全國代表大會代表總數２０％的不經預選而自動當選的所謂&ldquo;超級代表&rdquo;決定。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>新聞鏈接</strong></p>\r\n<p style=\\"text-indent: 2em\\">希拉裡支持率反超奧巴馬</p>\r\n<p style=\\"text-indent: 2em\\">領先7個百分點，是3個月來首次</p>\r\n<p style=\\"text-indent: 2em\\">新快報訊 根據《今日美國》最新全國民調結果，民主黨總統參選人希拉裡的支持率領先對手奧巴馬7個百分點，這是希拉裡三個月來，首次在全國民調中恢復領先地位。</p>\r\n<p style=\\"text-indent: 2em\\">(本文來源：金羊網-新快報 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (4, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<p style=\\"text-indent: 2em\\"><strong>新華網莫斯科5月7日電</strong> 普京7日在克里姆林宮與俄國家杜馬（議會下院）議會黨團領導人就他出任總理問題進行了磋商。</p>\r\n<p style=\\"text-indent: 2em\\">據俄塔社報道，普京表示，一旦國家杜馬批准他為政府總理，他將考慮議員們提出的各種意見。</p>\r\n<p style=\\"text-indent: 2em\\">普京說，大家彼此都非常熟悉，沒必要發表長篇大論。他認為，所有人的共同願望是，加強國力，以最有效的方式解決經濟和社會問題，提高工資和退休金，改善教育環境，加強國防，確保公民安全。</p>\r\n<p style=\\"text-indent: 2em\\">格雷茲洛夫、久加諾夫、日裡諾夫斯基、列維切夫分別代表統一俄羅斯黨、俄羅斯共產黨、自由民主黨和公正俄羅斯黨參加了磋商。</p>\r\n<p style=\\"text-indent: 2em\\">當天，俄新任總統梅德韋傑夫提名普京為新一屆政府總理。國家杜馬將於8日討論並批准普京出任總理的問題。 <hr />\r\n</p>\r\n<p>&nbsp;</p>\r\n<p style=\\"text-indent: 2em\\"><strong>據稱俄共將投票反對普京任總理 </strong> 據俄羅斯《信息報》報道，俄羅斯共產黨主席團已經採取決定，所有俄共杜馬黨團成員將在5月8日投票反對普京擔任總理。</p>\r\n<p style=\\"text-indent: 2em\\">新民網5月6日報道</p>\r\n<p style=\\"text-indent: 2em\\">不過，一些專家認為，雖然目前俄共是俄國家杜馬中第二大黨，但在國家杜馬全部450個議席中只佔有57個席位，很難有什麼決定權。而普京擔任主席的&ldquo;統一俄羅斯&rdquo;黨則控制著杜馬三分之二以上席位，可以輕易地使普京獲得批准。</p>\r\n<p style=\\"text-indent: 2em\\">普京第二任期內的總統職權將在新當選的俄羅斯總統梅德韋傑夫總統就職儀式舉行的當天，即5月7日到期。5月8日，俄國家杜馬將經過投票決定是否批准普京出任總理。 (本文來源：新華網 作者：盧敬利)</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (5, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<P style="TEXT-INDENT: 2em"><STRONG>重慶晨報5月8日報道</STRONG>&nbsp; 法國航空公司一名飛行員駕機途中讓一男孩到駕駛艙參觀,還為他「表演」高空特技,險些造成兩機相撞。法國航空安全部門已就此事展開調查。</P><P style="TEXT-INDENT: 2em"><STRONG>不適宜的炫技</STRONG></P><P style="TEXT-INDENT: 2em">英國《每日郵報》6日報道,法國航空公司一架空中客車A320客機3日上午從英國曼徹斯特飛往法國巴黎,上面有143名乘客。</P><P style="TEXT-INDENT: 2em">駕駛艙內,除了相關工作人員,竟然還有一名13歲的法國男孩。飛行途中,乘客突然覺得,飛機好像跳起了「搖擺舞」,左一下,右一下。</P><P style="TEXT-INDENT: 2em">現年40歲的乘客肖恩·魯濱遜來自英國蘭開夏郡羅森代爾。他告訴《每日郵報》記者：「飛行員毫無預警地駕駛飛機向左急轉,然後又猛地轉回來,顯然是在向法國男孩展示自己的駕機技術。」</P><P style="TEXT-INDENT: 2em"><STRONG>險釀兩機相撞</STRONG></P><P style="TEXT-INDENT: 2em">男孩離開駕駛艙幾分鐘後,飛行員通知乘客準備降落。突然,乘客聽到駕駛艙裡傳來警報聲。</P><P style="TEXT-INDENT: 2em">一名英國乘客說：「飛機引擎猛然加速,我們一下子往後上方彈去。」魯濱遜說,「飛行員操縱飛機急劇攀升。坐在我前面的兩名乘務員滿臉驚恐,緊緊抓住自己的座椅」,「飛機顯然遇上麻煩。也許飛行員注意力不集中,開了小差」。乘客們驚惶失措,大聲祈禱。</P><P style="TEXT-INDENT: 2em">飛機往上攀升1萬英尺(3048米)後才重新恢復平穩。飛行員向乘客解釋,當時空中交通管制部門通過無線電警告他,「往上拉,往上拉,往上拉1萬英尺,你快撞上其他飛機了」。20分鐘後,這架飛機安全降落在法國巴黎戴高樂機場。</P><P style="TEXT-INDENT: 2em">《每日郵報》說,法國航空公司已就男孩參觀駕駛艙和險些撞機兩事展開調查。公司發言人說,法航「極為嚴肅地」看待這一事件。法國運輸部門一名官員說,法國和英國都禁止乘客進入飛機駕駛艙。 (本文來源：華龍網-重慶晨報  作者：楊舒怡)');
INSERT INTO `phome_ecms_news_data_1` VALUES (6, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<center><object classid=\\"clsid:D27CDB6E-AE6D-11cf-96B8-444553540000\\" width=\\"480\\" height=\\"400\\" codebase=\\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,29,0\\">\r\n<param value=\\"12700\\" name=\\"_cx\\" />\r\n<param value=\\"10583\\" name=\\"_cy\\" />\r\n<param value=\\"\\" name=\\"FlashVars\\" />\r\n<param value=\\"http://orz.163.com/tools/shareplayer.swf?inputName=orz-1890242522_1210180592570.flv&amp;coverName=http://img.blog.163.com/photo/Njg5bkOoodhQl38a4V_dEg==/177329235327865088.jpg\\" name=\\"Movie\\" />\r\n<param value=\\"http://orz.163.com/tools/shareplayer.swf?inputName=orz-1890242522_1210180592570.flv&amp;coverName=http://img.blog.163.com/photo/Njg5bkOoodhQl38a4V_dEg==/177329235327865088.jpg\\" name=\\"Src\\" />\r\n<param value=\\"Window\\" name=\\"WMode\\" />\r\n<param value=\\"-1\\" name=\\"Play\\" />\r\n<param value=\\"-1\\" name=\\"Loop\\" />\r\n<param value=\\"High\\" name=\\"Quality\\" />\r\n<param value=\\"\\" name=\\"SAlign\\" />\r\n<param value=\\"-1\\" name=\\"Menu\\" />\r\n<param value=\\"\\" name=\\"Base\\" />\r\n<param value=\\"\\" name=\\"AllowScriptAccess\\" />\r\n<param value=\\"ShowAll\\" name=\\"Scale\\" />\r\n<param value=\\"0\\" name=\\"DeviceFont\\" />\r\n<param value=\\"0\\" name=\\"EmbedMovie\\" />\r\n<param value=\\"FFFFFF\\" name=\\"BGColor\\" />\r\n<param value=\\"\\" name=\\"SWRemote\\" />\r\n<param value=\\"\\" name=\\"MovieData\\" />\r\n<param value=\\"1\\" name=\\"SeamlessTabbing\\" />\r\n<param value=\\"0\\" name=\\"Profile\\" />\r\n<param value=\\"\\" name=\\"ProfileAddress\\" />\r\n<param value=\\"0\\" name=\\"ProfilePort\\" />\r\n<param value=\\"all\\" name=\\"AllowNetworking\\" />\r\n<param value=\\"true\\" name=\\"AllowFullScreen\\" /><embed width=\\"480\\" height=\\"400\\" allowfullscreen=\\"true\\" type=\\"application/x-shockwave-flash\\" pluginspage=\\"http://www.macromedia.com/go/getflashplayer\\" quality=\\"high\\" src=\\"http://orz.163.com/tools/shareplayer.swf?inputName=orz-1890242522_1210180592570.flv&amp;coverName=http://img.blog.163.com/photo/Njg5bkOoodhQl38a4V_dEg==/177329235327865088.jpg\\"></embed></object></center>\r\n<p style=\\"text-indent: 2em\\"><strong>中國新聞網5月6日電</strong> 5月6日早上，承擔著物種延續重任的長沙斑鱉&ldquo;姑娘&rdquo;在經過一千一百公里的長途奔波後終於平安抵達蘇州動物園，率先住進了&ldquo;新房&rdquo;，新郎則是蘇州的一隻斑鱉。</p>\r\n<p style=\\"text-indent: 2em\\">斑鱉是一種已擁有2.7億年歷史的古老物種，上世紀60年代以來，野生斑鱉就絕跡了。目前全球僅發現三隻存活的斑鱉，另外一隻在越南。</p>\r\n<p style=\\"text-indent: 2em\\">看到新媳婦，另一池塘中的蘇州斑鱉&ldquo;小伙&rdquo;顯得興奮不已，不時把頭探出水面張望，或者乾脆露出巨大的身軀和小魚嬉戲。&ldquo;新娘&rdquo;則顯得比較害羞。</p>\r\n<p style=\\"text-indent: 2em\\">它們的&ldquo;婚房&rdquo;是個約400平米的池塘，被一道隔離網分成相等的兩部分，南邊是一個用細沙鋪成的人工島，這將是它們繁衍後代的地方。隔離網中的活門打開後，&ldquo;夫妻倆&rdquo;便可親密接觸了。很多蘇州市民和遊客一早就守候在鱉池邊，期待一睹&ldquo;新娘&rdquo;的芳容。</p>\r\n<p style=\\"text-indent: 2em\\">據介紹，雖然這三隻斑鱉都已步入老年，但飼養得當還能活兩三百年。若這兩隻斑鱉能配對成功，將是保護斑鱉的最後希望。 (本文來源：中國新聞網 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (7, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<center><center><img alt=\\"\\" border=\\"0\\" src=\\"http://cimg21.163.com/cnews/2008/5/8/2008050801361639b32.jpg\\" /></center><center>地震發生地點示意圖。路透<br />\r\n</center><img alt=\\"\\" border=\\"0\\" src=\\"http://cimg20.163.com/cnews/2008/5/8/2008050801033418123.jpg\\" /></center><center>工作人員在展示地震計數據。</center>\r\n<p style=\\"text-indent: 2em\\"><strong>新華網北京5月8日電</strong> 據國家地震台網測定，北京時間2008年5月8日0時45分，在日本本州東海岸近海（北緯36．1度，東經141．6度）發生7．1級地震，已致使5人受輕傷。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>新華網東京5月8日電</strong>&nbsp; 據日本氣象廳觀測，日本本州以東海域當地時間8日凌晨1時45分（北京時間0時45分）發生裡氏6.7級地震。這是不到1小時內同一海域發生的第二次裡氏6級以上地震。</p>\r\n<p>　　氣象廳說，地震震中位於茨城縣以東海域，具體位置為北緯36.2度、東經141.7度，震源深度為40公里。地震震級為裡氏6.7級，北海道太平洋沿岸和本州東部地區都有震感，其中□木縣震感最強，東京也有明顯震感，晃動持續了近20秒。</p>\r\n<p>　　據日本媒體報道，目前尚無人員傷亡和或財產損失的消息，有關方面正在加緊進行受災確認工作。氣象廳說，此次地震造成海面潮位的一些變化，但不會造成海嘯。</p>\r\n<p>　　當地時間8日凌晨1時02分（北京時間0時02分），同一海域發生裡氏6.3級地震。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>新華網東京5月8日電</strong>&nbsp; 據日本氣象廳觀測，日本本州東部海域當地時間8日凌晨1時02分（北京時間8日凌晨0時02分）發生裡氏6.3級地震。</p>\r\n<p style=\\"text-indent: 2em\\">氣象廳說，此次地震的震中位於茨城縣以東海域，具體位置為北緯36.2度、東經142.1度，震源深度為20公里。地震震級為裡氏6.3級，關東及東北南部太平洋沿岸地區震感較大，東京也有明顯震感。</p>\r\n<p style=\\"text-indent: 2em\\">目前尚無關於人員傷亡和財產損失的報道。氣象廳說，此次地震不會造成海嘯。 (本文來源：新華網 作者：劉贊)</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (8, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<p>　　新華網仰光5月7日電（記者張雲飛）包括中國在內的部分國家人道主義援助物資7日緊急運抵緬甸仰光市，緬甸政府迅速將有關物資運往災區發放給災民。</p>\r\n<p>　　緬甸國家電台當天晚間廣播說，已經有7個國家將救災物資運送到緬甸，這些國家是日本、孟加拉國、老撾、中國、泰國、印度和新加坡。物資包括藥品、食品、帳篷、衣服和發電機等。</p>\r\n<p>　　緬甸一名官員7日說，各地的救災工作正在深入展開，有關救災物資已運到重災區，一些國際援助物資也正在陸續抵達緬甸。仰光市的救災恢復工作進展較快，大小街道基本可以通行，自來水已能送到居民樓，固定電話和供電設施正在搶修之中。人們生活仍有困難，但社會秩序基本正常。</p>\r\n<p>　　強熱帶風暴從2日清晨至3日夜間襲擊緬甸仰光省、伊洛瓦底省、勃固省、孟邦和克倫邦，造成重大人員傷亡和財產損失。最新統計數據顯示，在災難中死亡的人數已達22980人，另有42119人失蹤，1383人受傷。</p>\r\n<p>　　據中國駐緬甸大使館官員介紹，中國使館人員以及在緬中資機構人員、媒體工作者、留學生全部安全，使館迄今未接到有關華僑華人傷亡的報告。</p>\r\n<p>　　 (本文來源：新華網 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (9, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<p style=\\"text-indent: 2em\\">新華網聯合國5月7日電 聯合國糧食及農業組織7日表示，強熱帶風暴&ldquo;納爾吉斯&rdquo;給緬甸主要稻米產區造成重大損失，可能影響緬甸今年的稻米出口能力。</p>\r\n<p style=\\"text-indent: 2em\\">糧農組織預計，緬甸受災最嚴重5個省邦的稻米產量佔全國總產量的65%，水產品占總產量80%，家禽占50%。</p>\r\n<p style=\\"text-indent: 2em\\">糧農組織一份初步評估報告認定，緬甸的稻米、棕櫚油和橡膠種植業受創尤其嚴重。</p>\r\n<p style=\\"text-indent: 2em\\">據此間媒體報道，緬甸今年原定出口糙米60萬噸。受災情影響，出口計劃可能完成無望，且需從鄰國進口糧食，一進一出&ldquo;可能加劇全球稻米市場供應緊張&rdquo;。</p>\r\n<p style=\\"text-indent: 2em\\">澳大利亞麥誇裡大學緬甸問題專家肖恩&middot;特尼爾推測，今後18個月至24個月內，緬甸可能面臨&ldquo;難以置信的(糧食)短缺&rdquo;。</p>\r\n<p style=\\"text-indent: 2em\\">緬甸是世界主要稻米生產國，先前每年出口稻米數百萬噸。但是，為優先滿足國內需求，緬甸原計劃今年僅出口60萬噸。 (本文來源：新華網 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (10, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<P>　　<STRONG>新華網渥太華5月7日電</STRONG>&nbsp; 加拿大不列顛哥倫比亞省北溫哥華市警方7日說，該市一棟3層公寓樓6日晚發生火災，造成3人喪生，數十人受傷。</P><P>　　警方說，大火於6日21時許開始燃燒。消防人員趕到現場時，公寓樓的一些房間已被火苗吞噬。救援人員從陽台上救出6人，還有一些人自己從陽台跳下逃生。除3人被燒死外，有數十人摔傷或被燒傷，其中4人傷勢嚴重。</P><P>　　警方懷疑這是一起縱火案，已對此展開調查。</P><P>　　 (本文來源：新華網  作者：趙青)');
INSERT INTO `phome_ecms_news_data_1` VALUES (11, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<P style="TEXT-INDENT: 2em"><STRONG>新華網快訊：</STRONG>也門旅遊部長證實遭綁架的日本女遊客已獲釋。</P><P style="TEXT-INDENT: 2em"><STRONG>新華網東京5月8日電</STRONG> 據日本共同社8日報道，日本駐也門大使館7日證實，兩名日本女遊客當天在也門中部城鎮馬裡卜被綁架。</P><P style="TEXT-INDENT: 2em">也門當地一家網站稱，綁架日本遊客的是當地部族武裝成員，綁架者已要求也門政府釋放因殺害政府軍而被關押在首都薩那的同伴。</P><P style="TEXT-INDENT: 2em">據與兩名被綁架者同行的另兩名日本女遊客說，7日午飯後，4人分乘兩輛汽車前往馬裡卜大壩觀光。途中出現了武裝人員乘坐的卡車。兩名同行遊客感到危險而伏在車中，之後發現另兩人乘坐的汽車已經消失。</P><P style="TEXT-INDENT: 2em">另據報道，路邊檢查站的警察試圖阻止綁匪逃走時受到綁匪攻擊，導致一名警察受傷。</P><P style="TEXT-INDENT: 2em">日本外務省說，也門自1990年後共發生綁架外國人事件200起以上，多為地方武裝為和中央政府講條件而採取的手段。此前還沒有發生過被綁架外國人遇害的事件。</P><P style="TEXT-INDENT: 2em"><STRONG>新華網薩那5月7日電</STRONG> 也門警方7日說，當天有2名在中部城鎮馬裡卜旅遊的日本女遊客被綁架，綁架者可能是當地部落武裝。</P><P style="TEXT-INDENT: 2em">目擊者說，被綁架的2名日本女遊客是一個日本旅遊團的成員，她們當時正在遊覽古老的馬裡卜大壩。她們的也門導遊也被劫持。</P><P style="TEXT-INDENT: 2em">據報道，路邊檢查站的警察試圖阻止劫匪逃走時，雙方發生交火，一名警察受傷。</P><P style="TEXT-INDENT: 2em">近年來，也門針對外國遊客的綁架事件時有發生，多系當地部落武裝所為，不過大部分人質最終都安全獲釋。 (本文來源：新華網  )');
INSERT INTO `phome_ecms_news_data_1` VALUES (12, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<P>　　新華網快訊：愛爾蘭議會7日投票推舉副總理兼財政部長布賴恩·考恩為新一任政府總理。</P><P>　　(本文來源：新華網  )');
INSERT INTO `phome_ecms_news_data_1` VALUES (13, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<P style="TEXT-INDENT: 2em"><STRONG>新華網吉隆坡5月7日電 </STRONG>馬來西亞紅新月會7日在吉隆坡宣佈，該會將派出一個10人救災小組前往緬甸，幫助開展救災工作。</P><P style="TEXT-INDENT: 2em">馬紅新月會說，派遣救災小組事宜已獲得緬甸政府許可。救災小組將於8日啟程前往仰光，並帶去首批價值50萬林吉特（15．9萬美元）的炊具、毯子、蚊帳、蓆子和兒童醫藥用品等救災物資。</P><P style="TEXT-INDENT: 2em">馬紅新月會還表示，將通過在泰國的一些國際機構，向緬甸遭受強熱帶風暴襲擊地區陸續移交一些救災物資，並呼籲馬來西亞公眾向緬甸災區提供捐助。</P><P style="TEXT-INDENT: 2em">除了馬紅新月會之外，馬來西亞其他一些民間慈善團體已向緬甸派出救援人員，並正在準備向緬甸運送水淨化劑、藥品和防水布等救災物資。</P><P style="TEXT-INDENT: 2em">強熱帶風暴從2日清晨至3日夜間襲擊緬甸仰光省、伊洛瓦底省、勃固省、孟邦和克倫邦，造成重大人員傷亡和財產損失。據緬甸國家電台6日報道，截至6日中午，這次強熱帶風暴已造成約2．25萬人死亡，4．1萬人失蹤，其中受災最為嚴重的伊洛瓦底省死亡約2．2萬人，失蹤4萬多人。</P><P style="TEXT-INDENT: 2em">(本文來源：新華網  作者：熊平)');
INSERT INTO `phome_ecms_news_data_1` VALUES (14, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em"><CENTER><IMG src="http://cimg20.163.com/cnews/2008/5/5/200805050849166fee7.jpg" border=0></CENTER><P></P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em" align=center>烈火-3型中程導彈第一次試射時的畫面</P><P style="TEXT-INDENT: 2em"><CENTER><IMG src="http://cimg21.163.com/cnews/2008/1/28/2008012808145287aa8.jpg" border=0></CENTER><P></P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em" align=center>印度在共和日閱兵儀式上展出「烈火三」型導彈。</P><P style="TEXT-INDENT: 2em"><CENTER><IMG src="http://cimg2.163.com/cnews/2007/4/10/20070410101420e1b98.gif" border=0></CENTER><P></P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em" align=center>「烈火3」的射程可以涵蓋北京上海</P><P style="TEXT-INDENT: 2em"><STRONG>事件進展:</STRONG>印度軍方今早試射了一枚可攜載核彈頭的中程彈道導彈，射程足以涵蓋中國境內。今早試射的"烈火三型"導彈，射程3500公里，是印度軍方擁有的射程最遠的彈道導彈。</P><P style="TEXT-INDENT: 2em"><STRONG>新華網新德里5月7日電&nbsp;</STRONG>印度7日上午再次成功試射「烈火－3」型中程地對地彈道導彈。</P><P style="TEXT-INDENT: 2em">據《印度時報》網站報道，這次試射是當地時間7日上午9時55分在奧裡薩邦外海的惠勒島綜合試驗場進行的。導彈飛行15分鐘後，國防部官員宣佈試射成功。</P><P style="TEXT-INDENT: 2em">這是「烈火－3」型導彈第三次試射。該型導彈於2006年7月首次試射，導彈發射後失去控制墜入大海。第二次試射於去年4月12日進行，取得成功。</P><P style="TEXT-INDENT: 2em">「烈火－3」型導彈射程達3500公里，可攜帶核彈頭，被認為是印度最有威脅的中程地對地彈道導彈。</P><P style="TEXT-INDENT: 2em">(本文來源：新華網  作者：文建)');
INSERT INTO `phome_ecms_news_data_1` VALUES (15, 35, '', 1, 1, 0, 0, '', '未知', '網易', '<P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em"><CENTER><IMG src="http://cimg21.163.com/cnews/2008/5/7/200805072245130b04b.jpg" border=0></CENTER><P></P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em">5月7日，在緬甸仰光國際機場，緬甸的工作人員在搬運中國的援助物資。</P><P style="TEXT-INDENT: 2em">中國政府向緬甸政府提供的人道主義緊急援助物資當日下午空運抵達仰光國際機場。這批緊急物資主要包括帳篷、毛巾被和壓縮餅乾等，價值50萬美元。中國政府還向緬甸政府提供了50萬美元的現匯援助。（新華）</P><P style="TEXT-INDENT: 2em"><CENTER><IMG src="http://cimg20.163.com/cnews/2008/5/7/20080507224541cc92d.jpg" border=0></CENTER><P></P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em">5月7日，在緬甸仰光國際機場，中國駐緬甸大使管木（右二）與接受中國政府捐贈物資的緬甸社會福利、救濟和安置部部長貌貌瑞少將（右）握手。（新華）</P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em"><CENTER><IMG src="http://cimg20.163.com/cnews/2008/5/7/20080507224606aab55.jpg" border=0></CENTER><P></P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em">5月7日，在緬甸仰光國際機場，緬甸的工作人員在搬運中國的援助物資。</P><P style="TEXT-INDENT: 2em">中國政府向緬甸政府提供的人道主義緊急援助物資當日下午空運抵達仰光國際機場。這批緊急物資主要包括帳篷、毛巾被和壓縮餅乾等，價值50萬美元。中國政府還向緬甸政府提供了50萬美元的現匯援助。（新華）</P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em"><CENTER><IMG src="http://cimg20.163.com/cnews/2008/5/7/2008050722463139852.jpg" border=0></CENTER><P></P><P style="TEXT-INDENT: 2em"></P><P style="TEXT-INDENT: 2em">5月7日，在緬甸仰光國際機場，工作人員在搬運中國的援助物資。（新華）</P><P style="TEXT-INDENT: 2em">(本文來源：新華網  作者：張雲飛)');
INSERT INTO `phome_ecms_news_data_1` VALUES (61, 34, '', 1, 1, 0, 0, '', '未知', '網易', '<p style=\\"text-indent: 2em\\"><strong>中新網5月4日電</strong>&nbsp; 涉入侵吞台當局&ldquo;金援外交&rdquo;巨資醜聞的中間人吳思材驚爆遭兩名歹徒恐嚇要求離開台灣。台灣刑事部門3日晚間已針對全案展開調查，並24小時採取貼身保護的方式，確保吳思材的人身安全。</p>\r\n<p style=\\"text-indent: 2em\\">綜合台灣媒體報道，吳思材3日召開記者會，他的委任律師表示，4月26日下午，吳思材到他辦公室討論法律事宜，結束時吳獨自離開，隨即在一處路口遭兩名不明人士堵截，要他趕快離開台灣，否則生命會有危險，說完即掀開衣服亮出槍只。但亮槍警告吳思材的人，並沒有說是代表誰傳話。</p>\r\n<p style=\\"text-indent: 2em\\">台&ldquo;刑事局&rdquo;基於吳思材安全考慮，已調派維安特勤人員與偵查員全天候嚴密貼身保護，3日晚間並針對整個恐嚇細節約談吳思材，製作相關筆錄，展開偵查。</p>\r\n<p style=\\"text-indent: 2em\\">另外，台&ldquo;警政署&rdquo;表示，在台當局&ldquo;外交部長&rdquo;黃志芳也表達對家人遭受威脅的擔憂後，已立即指示相關轄區警方針對黃志芳家人住所加強巡邏勤務；目前黃志芳並未報案指稱有遭恐嚇，若有相關情事，也將派員提供必要的保護措施。(本文來源：中國新聞網 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (62, 34, '', 1, 1, 0, 0, '', '未知', '網易', '<center><img alt=\\"\\" border=\\"0\\" src=\\"http://cimg20.163.com/cnews/2008/5/4/200805040859225f40e.jpg\\" /><br />\r\n</center>\r\n<p align=\\"center\\" style=\\"text-indent: 2em\\">昨晚7點，武昌上空電閃雷鳴。武漢昨日共閃電2200次。本報記者孫辰攝</p>\r\n<center><img alt=\\"\\" border=\\"0\\" src=\\"http://cimg20.163.com/cnews/2008/5/4/20080504085933e2527.jpg\\" /><br />\r\n</center>\r\n<p align=\\"center\\" style=\\"text-indent: 2em\\">昨日凌晨1點，一場強雷暴&ldquo;意外&rdquo;襲擊江城。來源：武漢晚報熊波攝</p>\r\n<center><img alt=\\"\\" border=\\"0\\" src=\\"http://cimg20.163.com/cnews/2008/5/4/20080504085945fdfe8.jpg\\" /><br />\r\n</center>\r\n<p style=\\"text-indent: 2em\\">昨晚9時，我市突降大暴雨，鐵路立交橋下幾名路人打著傘在雨中急行。武漢晚報楊濤匡志達攝</p>\r\n<p style=\\"text-indent: 2em\\"><strong>157毫米！武漢一日內遭遇兩次暴雨襲擊</strong></p>\r\n<p style=\\"text-indent: 2em\\">暴雨突襲荊楚11縣市區，恩施千名乘客滯留機場，武漢城區嚴重漬水引發交通擁堵</p>\r\n<p style=\\"text-indent: 2em\\">5月2日至3日，我省出現今年以來第四次較強降雨過程。其中，50毫米以上的暴雨覆蓋黃陂、孝昌、紅安、京山、廣水等11個縣市區。據有關部門初步上報統計，截至昨日下午5時，孝感漢川、安陸兩地15萬畝農作物受災，民房倒塌26間，未出現人員及牲畜傷亡。</p>\r\n<p style=\\"text-indent: 2em\\">當陽遭遇特大雷雨暴風災害，致2人死亡直接經濟損失達3800多萬元。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>全省9個站達到大暴雨</strong></p>\r\n<p style=\\"text-indent: 2em\\">昨日全省大部分地區都出現了雷陣雨天氣，氣象部門雨量資料顯示，昨日全省共有52個觀測站達到暴雨，其中有9個站達到大暴雨。其中最大降雨量為135.7毫米，出現在孝感的孝南區。在荊門、石首、嘉魚、廣水等地還出現了7到10級的雷雨大風。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>孝感15萬畝農作物受災</strong></p>\r\n<p style=\\"text-indent: 2em\\">昨日晚上9時50分許，記者連線孝感市民政局救災科，據該部門工作人員介紹，自5月2日下午6點半以來，孝感全市普降大雨，孝感目前已啟動24小時應急預案，各縣區及各級民政部門24小時值班，密切關注此次降雨。</p>\r\n<p style=\\"text-indent: 2em\\">&ldquo;截至3日下午5點，漢川和安陸兩地受災明顯，主要是農作物有一定損失，漢川農作物受災面積在10萬畝左右，主要為油菜、小麥、棉花、蔬菜等農作物。安陸5萬畝農作物受災，主要出現油菜倒伏。此外，安陸有12戶26間民房倒塌。目前尚未出現人員和牲畜傷亡。&rdquo;</p>\r\n<p style=\\"text-indent: 2em\\">孝感市孝南區民政局張局長表示，目前該部門人員24小時值班，發現新的情況立即向上級部門匯報。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>暴雨致當陽2人死亡</strong></p>\r\n<p style=\\"text-indent: 2em\\">昨日下午3點至6時30分,宜昌當陽市遭受雷雨暴風的襲擊，瞬間極大風速達14.3米/秒,30分鐘內全市平均降水達到51毫米,其中城區降雨量達82.1毫米。10個鎮處均有不程度的災情，其中河溶、玉陽、壩陵、育溪等最為嚴重，這次災害造成當陽市直接經濟損失3800多萬元，其中農業經濟損失2100多萬元。全市因災緊急轉移安置人口300多人,倒塌民房89戶172間。</p>\r\n<p style=\\"text-indent: 2em\\">此次災害造成當陽市10000多人受災，因房屋倒塌死亡1人，因災受傷12人。河溶鎮紅日村三組村民付官香（女，51歲）被倒塌的房屋壓死，壩陵木林村一組翟中軍(男,51歲)因大風揭頂上房堵漏時意外摔傷,在送往醫院途中死亡。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>江城全天閃電2200次&ldquo;潑水&rdquo;157毫米</strong></p>\r\n<p style=\\"text-indent: 2em\\">前日晚上11時以後，江城突然狂風大作。昨日零時開始，伴隨著閃電和轟隆隆的雷聲，漢口和武昌最先開始下起暴雨。截至昨日中午，不同程度的暴雨還在持續。</p>\r\n<p style=\\"text-indent: 2em\\">截止到昨日下午5時，漢口東西湖的觀測站降雨量達到64毫米。氣象部門稱這是江城今年的首場暴雨。</p>\r\n<p style=\\"text-indent: 2em\\">昨日晚上6時42分許，武漢中心氣象台發佈短時預警信號，預計未來3小時武漢市有強雷陣雨，雨量30&mdash;50毫米。</p>\r\n<p style=\\"text-indent: 2em\\">截止昨日22時許，漢口東西湖觀測站顯示降雨量為157.4毫米。也就是昨日17時至22時的5個小時之間，江城又降雨93.4毫米（17時之前該觀測站顯示降雨量為64毫米），專家稱，其中21時至22時一個小時的降雨量為40.9毫米，該降雨量接近武漢從昨日零時至17時這17個小時的總降雨量，強度非常大。</p>\r\n<p style=\\"text-indent: 2em\\">據氣象部門統計，昨日江城這次降雨過程閃電次數共達到2200次。長江商報(本文來源：中國經濟網 作者：吳睿喻曉璐譚經田張勇軍)</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (63, 34, '', 1, 1, 0, 0, '', '未知', '網易', '<p style=\\"text-indent: 2em\\"><strong>中新網5月4日電</strong> 據香港文匯報報道，近日，包括安徽、廣東、湖北、陝西、浙江等省份相繼爆出手足口病疫情，引起中央高度關注。其中安徽阜陽已有22人死亡，而鄰近香港的廣東佛山也有2名病例死亡。而截至目前，香港今年也已出現10宗該病感染個案，直逼去年全年12宗的總數。</p>\r\n<p style=\\"text-indent: 2em\\">衛生部於3日凌晨發出緊急通知，將手足口病納入傳染病管理，要求各地開始24小時網絡直報。官方預計，該病將在未來兩月達到發病高峰，部分地區疫情仍可能上升。</p>\r\n<p style=\\"text-indent: 2em\\">衛生部在&ldquo;五一&rdquo;假期間連續下發了多個文件，要求各地高度重視對手足口疫情的防控，保障北京奧運順利舉行，維護社會穩定。</p>\r\n<p style=\\"text-indent: 2em\\">衛生部要求各地自5月2日開始24小時直報疫情，確保公眾的知情權，並修改了該病防控指南，還對兒科專家進行診療技術培訓。衛生部目前正在總結國內外手足口病防控和臨床救治經驗，組織開展流行病學、病原學等方面的深入研究。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>異種病毒威脅香港</strong></p>\r\n<p style=\\"text-indent: 2em\\">EV71型腸病毒是引致手足口病的病原體之一，香港現已踏入感染高峰期，早前香港基督教服務處觀塘幼兒學校，在兩星期內有23名學童出現手足口病的病徵，該校需停課兩星期。香港衛生防護中心2日一日內接獲3宗EV71型腸道病毒個案，其中包括一對一起&ldquo;中招&rdquo;的母女。</p>\r\n<p style=\\"text-indent: 2em\\">香港衛生防護中心總監曾浩輝3日出席活動後表示，今年EV71型腸病毒較往年活躍，香港至今已出現10宗個案，&ldquo;預期踏入夏天是高峰期，會出現更多個案。&rdquo;</p>\r\n<p style=\\"text-indent: 2em\\">曾浩輝說，EV71型腸病毒主要感染小孩，引致發燒、無胃口、口腔出現水皮包等病徵，同時該病毒較其它腸病毒更大機會引起併發症，例如腦炎、心肌炎等。他強調，暫未發現EV71型腸病毒有變種跡象，也未見到會有大規模爆發。他提醒家長，必須注意個人及子女衛生。</p>\r\n<p style=\\"text-indent: 2em\\">傳染病專科醫生勞永樂表示，由於週期性循環，隔數年便會出現一次較大規模的EV71型腸病毒感染，&ldquo;小區中累積了一班沒有免疫力的市民，便容易出現一次較厲害的爆發。&rdquo;他指出，有人感染EV71型腸病毒後不會出現病徵，但由於傳染力強，令人防不勝防。</p>\r\n<p style=\\"text-indent: 2em\\">港大感染及傳染病中心總監何柏良表示，內地及香港普遍出現的EV71型腸病毒，型號與東南亞流行的不同，擔心東南亞的病毒傳入香港，兒童沒有免疫，感染人數可能會上升：&ldquo;過去10年，每1、2年，(亞洲)便有較大的爆發，這些大型的爆發，往往會有很多小童，如影響幾百、幾千小童，當中會有10%至30%有併發症，要到醫院、深切治療甚至死亡。&rdquo;</p>\r\n<p style=\\"text-indent: 2em\\"><strong>港發病率高於過去3年</strong></p>\r\n<p style=\\"text-indent: 2em\\">同時，香港衛生防護中心社會醫學顧問醫生張竹君日前向醫生發出通信，指今年EV71型腸病毒感染個案較過去3年同期為多，並指夏天是手足口病的發病高峰期，呼籲醫生向家長提供健康信息，一旦學童證實染病，在病癒後仍應暫停上學兩星期。</p>\r\n<p style=\\"text-indent: 2em\\">香港醫管局新界東醫院聯網總監馮康4日出席公開活動後表示，有關單位已提醒所有當值醫生，如發現有類似手足口徵狀的病人要盡快治療，醫院也會加強感染控制措施。香港中文大學醫學院院長霍泰輝則表示，香港暫時沒有EV71型腸病毒爆發跡象，但市民仍要做足預防措施，老師也要提醒學童多洗手。</p>\r\n<p style=\\"text-indent: 2em\\">據悉，今年以來，在新加坡和中國台灣地區的手足口病疫情比去年同期明顯上升，而澳門在近日接獲一家托兒所7名幼童患手足口病的報告，其中5名同班。(本文來源：中國新聞網 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (64, 34, '', 1, 1, 0, 0, '', '未知', '網易', '<p style=\\"text-indent: 2em\\"><strong>中新網5月4日電</strong>&nbsp; 民進黨今天(4日)下午召開臨時黨代表大會，對敗選後黨內檢討總結，台當局&ldquo;副總統&rdquo;呂秀蓮、黨主席謝長廷、&ldquo;行政院長&rdquo;張俊雄及兩位前黨主席蘇貞昌及游錫坤等巨頭都將出席，唯獨陳水扁選擇缺席。</p>\r\n<p style=\\"text-indent: 2em\\">據台灣聯合報報道，自&ldquo;大選&rdquo;敗選後，陳水扁迴避所有的黨務活動及一系列敗選檢討會，連黨最高權力機構的全代會要對敗選做最做總結及通過改革方案，他也不參加。此舉會否在今天的臨全會引爆全面反彈，黨內頗憂心。</p>\r\n<p style=\\"text-indent: 2em\\">不過為使會議&ldquo;單純化&rdquo;，民進黨中央特別安排三位黨主席候選人辜寬敏、蔡英文和蔡同榮在大會的開幕式，各發表六分鐘的演講，希望把大會聚焦在三位黨主席的理念闡述上。</p>\r\n<p style=\\"text-indent: 2em\\">大會討論時間主要是處理民進黨選後舉辦多場檢討會所通過的共同意見，以及黨代表提出的改革提案。其中&ldquo;排藍民調&rdquo;經討論後雖已由黨中執會通過廢除，但是黨內反對聲音仍在，預料這個問題將是今天會上的不定時炸彈。</p>\r\n<p style=\\"text-indent: 2em\\">(本文來源：中國新聞網 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (65, 34, '', 1, 1, 0, 0, '', '未知', '網易', '<center><img alt=\\"\\" border=\\"0\\" src=\\"http://cimg20.163.com/cnews/2008/5/4/20080504084507c011d.jpg\\" /><br />\r\n</center>\r\n<p style=\\"text-indent: 2em\\">知名作家柏楊四月二十九日凌晨一時十二分，病逝於台灣新店耕莘醫院，享壽八十九歲。據&ldquo;中央社&rdquo;報道，柏楊二月二十四日因肺炎並呼吸衰竭住進加護病房，三月十九日轉入普通病房，後來又因胃部問題進行&ldquo;胃造廔&rdquo;手術，再次住進加護病房，四月十二日才轉普通病房，以氧氣及呼吸輔助器治療。柏楊，1920年出生於河南，畢業於東北大學，曾任自立晚報副總編輯及公立藝專教授。柏楊著作等身，知名作品包括《醜陋的中國人》、《中國人史綱》、《柏楊版資治通鑒》等。資料圖為柏楊和他的漫畫形象。中新社發陳國華攝</p>\r\n<p style=\\"text-indent: 2em\\"><strong>東方早報5月4日報道</strong> 柏楊家人日前對外宣佈，柏楊葬禮將於5月14日舉行，骨灰將撒在他曾經被囚禁多年的綠島海面。</p>\r\n<p style=\\"text-indent: 2em\\">據柏楊家人介紹，5月14日上午將舉行家祭，下午將在台北濟南教會的教堂裡為柏楊舉行&ldquo;安息禮拜&rdquo;活動，教堂有300個座位，不發請柬，柏楊家人只在報紙上發訃告，但台灣甚至海外各界名流會到場共同紀念這位標誌性的文化大師。至於具體到場人員名單，柏楊家人表示不便透露。當日紀念活動結束後，柏楊的遺體將送去火化。</p>\r\n<p style=\\"text-indent: 2em\\">5月17日，如果台灣天氣晴好，親人將遵照他生前的遺願，把骨灰送往他昔日曾經坐牢的綠島附近海面拋撒。</p>\r\n<p style=\\"text-indent: 2em\\">在柏楊離世的這兩天裡，來自世界各地的唁電和慰問信雪片般飛到台北縣的家中，柏楊家人對人們的關心表示感謝。</p>\r\n<p style=\\"text-indent: 2em\\">綠島是台灣台東縣往東18海里的一個小島，那個地域，曾經是柏楊步入的地獄，也是他人生中的煉獄，後來又成為他人生的轉折點，柏楊對那裡一直懷著複雜的感情，他在那裡坐牢長達9年26天。當年所受的屈辱，柏楊都是淡然視之。柏楊次女回憶，作家曾給女兒講自己被從綠島釋放的情景：有一天，柏楊正在監獄圖書館讀書，監獄的感訓組長汪用效走進來，把他拉到院子裡說：&ldquo;告訴你一個好消息！&rdquo;然後拿出一份警備司令部的公文，上面寫著：&ldquo;柏楊一員，本部另委工作，即日派員陪伴返台。希轉知。&rdquo;當時柏楊捧著這紙公文，欣喜若狂，他終於有了自由身。 (本文來源：中國新聞網 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (66, 34, '', 1, 1, 0, 0, '', '未知', '網易', '<p style=\\"text-indent: 2em\\">昨晚(5月3日)，廣東省物價局就近期糧價情況以及所謂節後糧價要漲的消息作出回應。省物價局監測報告以及五一期間市場檢查顯示，目前廣東大米批零市場供應正常，僅個別品種國產優質米零售價格漲幅較大，進口泰國米零售價格與節前持平。同時，省物價局昨日表示，吉之島已表示個別媒體報道其銷售的大米計劃漲價消息不實，而且廣東省農調戶戶均存糧較去年多17.67%，因此，&ldquo;節後提價消息不可信&rdquo;，而且省物價局已對糧價啟動一日一報制度。</p>\r\n<p style=\\"text-indent: 2em\\">據廣東省物價局成本隊近期對全省38個縣576戶農調戶存糧調查顯示，戶均存糧達到548.81公斤，比上年增加17.67%，充分說明&ldquo;手中有糧、心中不慌&rdquo;。</p>\r\n<p style=\\"text-indent: 2em\\">省物價局昨日分析，由於我省市場經濟發達，居民消費心理比較成熟，面對米價上漲，能夠理性對待，目前沒有發現搶購和囤積行為。政府相關部門表示，國家已相應出台一系列措施，保證大米供應和價格平穩，廣東省實施的臨時價格干預措施以及糧食儲備政策，都將確保糧食的供應和價格穩定。</p>\r\n<p style=\\"text-indent: 2em\\">(記者李婧通訊員岳佳綜)</p>\r\n<p style=\\"text-indent: 2em\\">(本文來源：廣州日報 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (67, 34, '', 1, 1, 0, 0, '', '未知', '網易', '<p style=\\"text-indent: 2em\\">&nbsp;</p>\r\n<center><img border=\\"0\\" alt=\\"\\" src=\\"http://cimg20.163.com/cnews/2008/5/4/200805040841111f981.jpg\\" /></center>\r\n<p>&nbsp;</p>\r\n<p align=\\"center\\" style=\\"text-indent: 2em\\">2日，杭州灣大橋北岸收費站外，車輛排隊等候通過。（來源：浙江在線）</p>\r\n<p style=\\"text-indent: 2em\\">&nbsp;</p>\r\n<center><img border=\\"0\\" alt=\\"\\" src=\\"http://cimg21.163.com/cnews/2008/5/4/2008050408404294c79.jpg\\" /></center>\r\n<p>&nbsp;</p>\r\n<p style=\\"text-indent: 2em\\">2日下午6時左右，杭州灣大橋北連接線仍然非常擁堵，一位焦急的女性從汽車天窗探出身向前張望。記者吳磊（來源：解放網）</p>\r\n<p style=\\"text-indent: 2em\\"><strong>浙江在線5月4日報道</strong> 在經歷通車首日的嚴重擁堵後，昨（3）日，杭州灣跨海大橋迎來通暢的一天，旅客終於享受到了大橋帶來的便捷。但記者也發現，雖然相關部門再三提醒，大橋上不准停車觀光，但依然有人置若罔聞，有的甚至因此險些釀成危險事故。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>20分鐘穿越整座大橋</strong></p>\r\n<p style=\\"text-indent: 2em\\">記者昨天下午5時左右開始從跨海大橋北接線上橋，從此處直到上橋處，一路車流稀少、行駛通暢，10分鐘後記者順利上橋。上橋後依然一路平坦，約20分鐘記者橫跨了整座大橋。而前天本報記者在同樣的路段行車卻花費了1個多小時。隨後記者從寧波至上海方向過橋，依然只花費20分鐘左右。昨天傍晚，不僅記者感覺大橋通暢，連上海長途客運南站相關人士也告訴記者，從昨天早上至下午3時左右，該站100多班通過杭州灣大橋的客運班次運行正常。司機向調度室反映，跨海大橋路況通暢，從寧波至上海只需2個半小時。</p>\r\n<p style=\\"text-indent: 2em\\">據大橋管理局介紹，截至昨天中午的詳細數據，大橋通行車輛已達82000多車次，加上下午和晚上的通行車輛，大橋開通頭兩天日均約有逾10萬多車次通行。在通行首日時，很大一部分車輛是為&ldquo;嘗鮮&rdquo;而來，所以不僅車流量很大，且這些車輛常會在橋上停車觀光，成為大橋擁堵的重要原因。</p>\r\n<p style=\\"text-indent: 2em\\">昨天是&ldquo;五一&rdquo;假期的最後一天，不少人選擇在家休息，從而緩解了車流壓力。同時，相關部門通過不同方式建議駕車者選擇&ldquo;老路&rdquo;通行。比如從寧波到上海會建議駕車者走原來的滬杭甬高速。這樣的措施，也為大橋分流了大量旅遊回歸客流。</p>\r\n<p style=\\"text-indent: 2em\\"><strong>違規停車觀光險情頻現</strong></p>\r\n<p style=\\"text-indent: 2em\\">記者發現，雖然大橋和大橋附近路段車輛運行通暢，但部分駕駛員在橋上停車觀光的現象依然存在。</p>\r\n<p style=\\"text-indent: 2em\\">大橋中段一處相對較寬處，成為違規停車觀光的&ldquo;重災區&rdquo;。記者昨天下午5點20分左右趕到此處時，有數十輛車輛擅自停靠在路邊，上百名乘客下車拍照觀光。有些乘客為了拍照，站在第三車道上來回行走，還奔跑嬉戲，全然不顧身邊飛馳而過的車輛。</p>\r\n<p style=\\"text-indent: 2em\\">不少乘客還讓自己的小孩也隨意下車玩耍，來自金華的關先生就差點為此付出&ldquo;代價&rdquo;。他剛把自己年幼的兒子小關抱下車，由於看到一望無際的海水，興奮的小關猛然掙脫爸爸的手，直撲大橋路邊的防護欄。小關嬌小的身軀眼看就有從防護欄中間的縫隙中穿過墜海的危險。&ldquo;快回來&hellip;&hellip;&rdquo;關先生此時已無法拉住小關，嚇得不由地叫出了聲。幸好邊上一位乘客伸手抱住了小關。但這樣驚險的一幕，並沒有有效提醒周邊的乘客，依然有人讓小孩自己爬到防護欄上拍照留影。</p>\r\n<p style=\\"text-indent: 2em\\">據瞭解，為了防止此類現象不斷發生，高速交警已利用了隨車配備的數碼攝像機和照相機進行交通違法非現場取證。同時高速交警、大橋管理局、路政、養護、排障中心出動共計40輛車在大橋上聯合巡查。管理部門呼籲大橋上駕駛車輛一定要文明行車、安全行車。（文/新聞晨報） (本文來源：浙江在線 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (68, 34, '', 1, 1, 0, 0, '', '未知', '網易', '<p style=\\"text-indent: 2em\\">中廣網北京5月4消息 中國駐日本大使崔天凱在人民日報撰文指出：胡錦濤主席訪日有三點尤其值得關注。</p>\r\n<p style=\\"text-indent: 2em\\">5月6日至10日，中國國家主席胡錦濤將對日本進行國事訪問。這是中國國家元首時隔十年再次訪日，也是新世紀首次訪日，同時也是中國最高領導人在中共十七大之後的首次出訪。胡主席的這次歷史性訪問具有重要象徵意義，充分體現了中國政府和領導人對友好近鄰日本的高度重視和進一步發展中日友好合作關係的積極意願。我們希望通過此次訪問，與日方在增進戰略互信、深化互利合作、改善國民感情、建立和完善各領域交流與合作機制、加強彼此在地區和國際事務中的協調合作等方面進行深入探討，並就此達成廣泛共識，真正將兩國關係納入長期健康穩定發展的軌道。我認為，胡主席此訪目標明確，內涵豐富，其中有三點尤其值得關注。</p>\r\n<p style=\\"text-indent: 2em\\">一是確立穩定的戰略互信。當前中日關係正處在改善發展的重要時期，兩國各自都在經歷深刻而複雜的變化。如何盡快構築起穩定的戰略互信，是雙方拓展現實利益、謀求長遠發展過程中面臨的一項重要而緊迫的課題。這一課題的核心，是如何以客觀、冷靜、健康、友善的心態看待和應對對方的發展變化，努力開創互利雙贏的新局面。中國改革開放事業已經走過30年不平凡的歷程，我們在此過程中不僅通過與世界各國的平等互利合作發展了自己，也以自己的發展為世界的穩定與發展作出了重要貢獻。在深入推進改革開放戰略的歷史進程中，中方願繼續將日本視為和平發展、互利共贏的重要合作夥伴。胡主席此次訪日期間，將重點向日本政府和日本人民傳遞一個明確信息，即和平發展是聯結中日兩國前途命運的重要紐帶，雙方不僅需要而且能夠以此為基礎確立穩定的戰略互信，為兩國關係長期健康穩定發展提供強有力的政治保障。</p>\r\n<p style=\\"text-indent: 2em\\">二是推進更高層次的互利合作。中日邦交正常化以來，兩國經濟合作規模不斷擴大，相互依存日益加深，給兩國人民帶來重要利益。2007年雙邊貿易額達2360億美元，中國是日本最大貿易夥伴，日本則是中國第三大貿易夥伴。日本對華實際投資金額累計達607億美元，是中國第二大外資來源地。同時中國經濟的快速發展，也給日本帶來了重要機遇，成為拉動日本經濟走出低谷、轉向穩定增長的重要外部促進因素。兩國互利合作不僅規模巨大而且潛力無限，雙方應結合自身發展需要和當前國際經濟形勢，以節能環保、高新技術、金融等領域為重點，開展更高水平和層次的新型合作。同時，隨著經濟全球化和地區一體化的發展，中日合作的面不斷拓寬，在地區和國際多邊事務中的共同利益和共同責任日益增多。福田首相去年底訪華時表示，中日兩國歷史上從來沒有像今天這樣有力量為亞洲及世界的穩定與發展作出貢獻。他的這番話表明，歷史正在將中國和日本推向新的舞台。胡主席此訪期間，我們將與日方就應對氣候變化、防範金融風險、推動亞洲合作、維護食品安全等全球和地區議題深入探討，積極研究開展互利合作的有效途徑和可能性。我們認為，中日雙方作為亞洲大國，應該以更加積極的姿態，進行更多建設性互動，為建設一個和諧而充滿活力的世界共同作出應有的貢獻。</p>\r\n<p style=\\"text-indent: 2em\\">三是進一步增進國民感情。中日比鄰而居，兩國人民之間的友好往來源遠流長，兩國共同的歷史文化傳統成為聯繫兩國人民心靈的重要橋樑和紐帶，對增進兩國國民感情具有不可替代的重要作用。我認為，當前形勢下，雙方必須切實加強兩國人民特別是青少年之間的友好交流。胡錦濤主席本人很早就直接從事並指導中日青少年交流工作，在日本各界廣交朋友。去年6月和今年3月，他曾先後兩次在緊張繁忙的公務中特意抽出時間，直接參與日本青少年訪華交流活動，揮毫題詞&ldquo;舉青春之力謀世代友好&rdquo;。這是他作為中國最高領導人以實際行動為中日兩國各界作出的重要表率。今年是《中日和平友好條約》締結30週年，也是中日青少年友好交流年。這是雙方重溫兩國關係發展歷程，弘揚和平友好精神，增進兩國國民感情，培養中日友好事業接班人的重要契機。我們將通過胡主席此次訪問，努力把兩國青少年友好交流推向新的高潮，推動兩國更多青少年投身到中日世代友好的偉大事業中去。</p>\r\n<p style=\\"text-indent: 2em\\">最近一段以來，中日關係中出現了一些值得關注的新動向，我認為這總體上是雙方交流與合作不斷向縱深發展伴隨的自然現象。雙方對有關問題應予高度重視，並通過對話協商，積極尋求相互理解和妥善解決，不宜只見樹木，不見森林，迷失主流和方向。我們要始終銘記&ldquo;中日友好是兩國人民之福，中日交惡是兩國人民之禍&rdquo;，始終以兩國人民長遠利益為重，把握好中日關係的發展方向。歷史的征程是漫長的，而決定方向和結果的往往是關鍵的幾步。</p>\r\n<p style=\\"text-indent: 2em\\">我們期待胡主席即將對日本進行的國事訪問取得圓滿成功，成為中日關係未來發展新的重要起點。我們願與日方共同努力，開創兩國戰略互惠關係新時代。(本文來源：中廣網 )</p>');
INSERT INTO `phome_ecms_news_data_1` VALUES (69, 34, '', 1, 1, 0, 0, '', '未知', '網易', '<center><center><img alt=\\"&ldquo;青銅劍&rdquo;&ldquo;鑲嵌&rdquo;在曬布巖巖壁上。\\" border=\\"0\\" src=\\"http://cimg21.163.com/cnews/2008/5/4/2008050408360409d5b.jpg\\" /><br />\r\n&ldquo;青銅劍&rdquo;&ldquo;鑲嵌&rdquo;在曬布巖巖壁上。</center><img alt=\\"&ldquo;青銅劍&rdquo;疑似綠色植物攀附在巖壁上生長。\\" border=\\"0\\" src=\\"http://cimg21.163.com/cnews/2008/5/4/200805040835517a68b.jpg\\" /><br />\r\n&ldquo;青銅劍&rdquo;疑似綠色植物攀附在巖壁上生長。</center>\r\n<p>　　中新網韶關5月3日電（記者馮昶袁少洪）3日下午，記者陪同遠道而來的朋友遊覽世界地質公園廣東丹霞山，在陽元石景區內，突然發現一把巨型的&ldquo;青銅劍&rdquo;。</p>\r\n<p>　　記者曾經不下數十次踏足丹霞山陽元石景區，但目睹此&ldquo;青銅劍&rdquo;之風貌，今天還是第一次。</p>\r\n<p>　　&ldquo;青銅劍&rdquo;把手朝上&ldquo;鑲嵌&rdquo;在名叫曬布巖的陽元山大石牆紅砂岩巖壁上，記者根據曬布巖的高度粗略估算，這把巨&ldquo;劍&rdquo;連把手約有50米長，劍身寬也有好幾米。認真觀察，&ldquo;青銅劍&rdquo;疑似綠色植物攀附在巖壁上生長而成。至於為何會生成劍的模樣，又為何在春夏之交的季節出現，那真要請教專家了。</p>\r\n<p>　　記者把拍到的&ldquo;青銅劍&rdquo;照片給來丹霞山旅遊的眾多外地遊客觀看，眾人皆稱奇。(本文來源：中國新聞網 )</p>');
