INSERT INTO `phome_ecms_shop` VALUES (1, 46, 0, 2, 0, 0, '', '13551244391', 2, 'test', 0, 0, 1, 0, 0, 0, 0, 1355124446, 1355124446, 1, 0, 0, '', '/ecms72/shop/shoujishuma/13551244391.html', 1, 1, 1, '', '三星手機D848', 1355124446, 'http://www.gome.com.cn/upload/2008/1/22/120_120_20080122031412264.jpg', '', '三星', '這是一款可拍照，可上網的26萬色高清晰彩屏手機。\r\n', '', '', '1546.00', '1546.00', 0, 100, 'http://www.gome.com.cn/upload/2008/1/22/mid_20080122031412264.jpg', '這是一款可拍照，可上網的26萬色高清晰彩屏手機。<br />*&nbsp;上市時間&nbsp;　2006&nbsp;<br />*&nbsp;網絡制式&nbsp;　GSM&nbsp;900/1800/1900&nbsp;<br />*&nbsp;手機類型&nbsp;　滑蓋&nbsp;<br />*&nbsp;主屏參數&nbsp;　彩屏,26萬色,TFT,240×320像素,2.1英吋&nbsp;<br />*&nbsp;內存容量&nbsp;　80M&nbsp;<br />*&nbsp;外殼顏色&nbsp;　黑色&nbsp;<br />*&nbsp;體積&nbsp;　99×51×11.9mm&nbsp;<br />*&nbsp;重量&nbsp;　100克&nbsp;<br />*&nbsp;基本功能&nbsp;<br />　　*&nbsp;鈴聲&nbsp;　64和弦,支持MP3鈴聲&nbsp;<br />　　*&nbsp;通訊錄&nbsp;　分組管理,共1000條&nbsp;<br />　　*&nbsp;信息功能&nbsp;　SMS短信,EMS短信,MMS短信,短信群發,E-mail收發,20條群發功能，短信黑名單&nbsp;<br />　　*&nbsp;輸入法&nbsp;　T9英文輸入法&nbsp;<br />　　*&nbsp;遊戲&nbsp;　內置遊戲,2&nbsp;個；Bobby&nbsp;Carrot,TimeRiderII&nbsp;<br />　　*&nbsp;辦公功能&nbsp;　支持Picsel文件瀏覽器功能,支持Word/Excel/PowerPoint等多種文檔的瀏覽&nbsp;<br />　　*&nbsp;主要功能&nbsp;　內置天線,時鐘,內置震動,情景模式,通話時間提示,錄音功能,待機圖片,動畫屏保功能,來電圖片識別,來電鈴聲識別&nbsp;<br />　　*&nbsp;附加功能&nbsp;　鬧鐘,關機鬧鐘,日曆,計算器,日程表,記事本,備忘錄,世界時鐘,貨幣換算,單位換算,定時器,秒錶,自動鍵盤鎖&nbsp;<br />　&nbsp;&nbsp;*&nbsp;數據應用功能&nbsp;<br />　　*&nbsp;藍牙&nbsp;　支持,內置；支持A2DP藍牙立體聲&nbsp;<br />　　*&nbsp;數據業務&nbsp;　GPRS&nbsp;Class&nbsp;10&nbsp;<br />　　*&nbsp;JAVA&nbsp;　Java&nbsp;MIDP&nbsp;2.0&nbsp;<br />　　*&nbsp;WAP上網&nbsp;　wap&nbsp;2.0&nbsp;<br />　　*&nbsp;數據線&nbsp;　支持數據線,支持mini-USB&nbsp;<br />　　*&nbsp;擴展卡&nbsp;　支持TransFlash卡&nbsp;<br />　　*&nbsp;其它數據功能&nbsp;　內置Modem,SyncML功能&nbsp;<br />　&nbsp;&nbsp;*&nbsp;多媒體娛樂功能&nbsp;<br />　　*&nbsp;攝像頭&nbsp;　內置攝像頭,200萬像素,CMOS傳感器,內置閃光燈&nbsp;<br />　　*&nbsp;拍攝功能描述&nbsp;　支持MPEG4、3GP格式視頻拍攝&nbsp;<br />　　*&nbsp;MP3播放器&nbsp;　支持,內置,支持MP3、AAC、AAC+、e-AAC+、WMA格式音樂播放,可自主設定播放列表，可設定重複模式以及隨機播放模式,均衡器(一般/古典/爵士/搖滾)和3D模式,內置雙揚聲器&nbsp;<br />　　*&nbsp;視頻播放&nbsp;　支持,支持MPEG4,3GP格式視頻播放&nbsp;<br />　　*&nbsp;多媒體&nbsp;　圖片編輯&nbsp;<br />　　*&nbsp;其它性能&nbsp;　支持TV-out視頻輸出&nbsp;', 0);
INSERT INTO `phome_ecms_shop` VALUES (2, 46, 0, 0, 0, 0, '', '13551244392', 2, 'test', 0, 0, 1, 0, 0, 0, 0, 1355124447, 1355124447, 1, 0, 0, '', '/ecms72/shop/shoujishuma/13551244392.html', 1, 1, 1, '', '諾基亞手機 6120', 1355124447, 'http://www.gome.com.cn/upload/2008/2/24/120_120_20080224101312939.jpg', '', '諾基亞', '', '', '', '1793.00', '1793.00', 0, 100, 'http://www.gome.com.cn/upload/2008/2/24/mid_20080224101312939.jpg', '主要功能<br />*&nbsp;通過發送和接收帶有附件的電子郵件掌控繁忙的生活&nbsp;<br />*&nbsp;使用攝像機捕捉全部動作&nbsp;<br />*&nbsp;使用帶有閃光燈且支持全景圖像的&nbsp;200&nbsp;萬像素照相機拍攝相片&nbsp;<br />*&nbsp;通過最高達&nbsp;2GB&nbsp;的可擴展存儲空間來儲存音樂、視頻片段和圖像&nbsp;<br />*&nbsp;借助實用的日曆和便捷的&nbsp;PC&nbsp;同步功能使萬事井井有條、有條不紊&nbsp;<br />*&nbsp;使用音樂播放器欣賞喜愛的音樂，在悅耳的旋律中放鬆心神，恢復活力&nbsp;<br />*&nbsp;採用鍍鉻拋光，成熟而經典的設計&nbsp;<br /><br />&nbsp;&nbsp;工作頻率<br />*&nbsp;EGSM&nbsp;850/900/1800/1900&nbsp;<br />&nbsp;&nbsp;尺寸<br />*&nbsp;體積：66&nbsp;立方厘米&nbsp;<br />*&nbsp;重量：89&nbsp;克&nbsp;<br />*&nbsp;尺寸：105&nbsp;x&nbsp;46&nbsp;x&nbsp;15&nbsp;毫米&nbsp;<br />&nbsp;&nbsp;屏幕<br />*&nbsp;大屏幕：QVGA、TFT、1600&nbsp;萬真彩色、320&nbsp;x&nbsp;240&nbsp;像素、2&nbsp;英吋&nbsp;<br />*&nbsp;當前待機模式&nbsp;<br />*&nbsp;自動鍵盤鎖&nbsp;<br />&nbsp;&nbsp;用戶界面<br />*&nbsp;用戶界面基於&nbsp;Symbian&nbsp;操作系統的&nbsp;60&nbsp;系列軟件&nbsp;<br />&nbsp;&nbsp;圖像功能<br />*&nbsp;在橫向模式下使用&nbsp;200&nbsp;萬像素照相機&nbsp;<br />*&nbsp;數字變焦&nbsp;（4&nbsp;倍）&nbsp;和照相機閃光燈&nbsp;<br />*&nbsp;播放採用&nbsp;H.264&nbsp;（MPEG4）、3gpp、Real&nbsp;編解碼器的影音流媒體&nbsp;<br />*&nbsp;錄製&nbsp;H.263&nbsp;（3gpp）&nbsp;和&nbsp;MPEG4&nbsp;格式的視頻片段&nbsp;<br />&nbsp;&nbsp;多媒體功能<br />*&nbsp;支持&nbsp;MP3、M4A、eAAC+&nbsp;和&nbsp;WMA&nbsp;的音樂播放器&nbsp;<br />*&nbsp;支持播放符合&nbsp;3GPP&nbsp;標準的影音流媒體&nbsp;<br />*&nbsp;鈴聲：64&nbsp;和弦合成音&nbsp;MIDI&nbsp;鈴聲、原音鈴聲和&nbsp;MP3/AAC&nbsp;鈴聲&nbsp;<br />*&nbsp;視頻鈴聲&nbsp;<br />&nbsp;&nbsp;存儲功能<br />*&nbsp;高達&nbsp;35&nbsp;MB&nbsp;的用戶可用存儲空間&nbsp;<br />*&nbsp;通過&nbsp;micro&nbsp;SD&nbsp;存儲卡插槽可將存儲空間擴展至最高&nbsp;2&nbsp;GB&nbsp;<br />&nbsp;&nbsp;信息功能<br />*&nbsp;在蜂窩網絡中實現按鍵通話&nbsp;<br />*&nbsp;諾基亞&nbsp;Xpress&nbsp;聲音信息（AMS）–可便捷快速地通過彩信發送語*&nbsp;音或聲音片段&nbsp;<br />*&nbsp;短信息、彩信和電子郵件&nbsp;<br />*&nbsp;即時信息（包括支持顯示狀態信息的增強型動態名片夾）&nbsp;<br />&nbsp;&nbsp;連接功能<br />*&nbsp;第&nbsp;2.0&nbsp;版藍牙規範&nbsp;（SIM&nbsp;卡接入模式、耳機模式、免提模式和&nbsp;A2DP&nbsp;模式）&nbsp;<br />*&nbsp;支持大容量存儲等級的全速率&nbsp;mini&nbsp;USB&nbsp;插孔&nbsp;<br />*&nbsp;使用諾基亞&nbsp;PC&nbsp;套件與&nbsp;PC&nbsp;進行本地同步&nbsp;<br />&nbsp;&nbsp;網絡瀏覽<br />*&nbsp;DTM&nbsp;等級&nbsp;Class&nbsp;11&nbsp;（可通過&nbsp;EGPRS&nbsp;同時進行語音通話和互聯網瀏覽）&nbsp;<br />*&nbsp;支持&nbsp;OMA&nbsp;（開放移動聯盟）&nbsp;DRM&nbsp;（數字版權管理）&nbsp;2.0，以實現安全的內容下載<br />*&nbsp;註：此功能實現需網絡運營商支持。<br />&nbsp;&nbsp;數據傳輸<br />*&nbsp;EDGE/GPRS&nbsp;多時隙等級&nbsp;Class&nbsp;32&nbsp;<br />*&nbsp;允許通過藍牙功能進行離線模式傳送–只需要&nbsp;1&nbsp;張&nbsp;SIM&nbsp;卡&nbsp;<br />&nbsp;&nbsp;語音功能<br />*&nbsp;聲控撥號&nbsp;<br />*&nbsp;語音合成：使用語音合成可讓手機大聲讀出您的信息，語音合成是一種可讓您聽到文字信息、彩信和電子郵件的工具。&nbsp;<br />*&nbsp;聲控命令&nbsp;<br />*&nbsp;錄音器&nbsp;<br /><br />&nbsp;&nbsp;標準銷售包裝內容<br />*&nbsp;諾基亞&nbsp;6120&nbsp;c&nbsp;手機&nbsp;<br />*&nbsp;諾基亞旅行充電器&nbsp;AC-4&nbsp;<br />*&nbsp;諾基亞電池&nbsp;BL-5B&nbsp;（890&nbsp;毫安時）&nbsp;<br />*&nbsp;《用戶手冊》精簡版&nbsp;（在線查看《用戶手冊》完整版&nbsp;+&nbsp;上下文相關幫助功能）&nbsp;<br />*&nbsp;諾基亞立體聲耳機&nbsp;HS-47&nbsp;<br />*&nbsp;CD-ROM&nbsp;光盤&nbsp;<br />*&nbsp;商品資料僅供參考，以賣場實物為準', 0);
